INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '1、特种设备安全工作应当坚持安全第一、预防为主、节能环保、综合治理的原则。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '2、县级以上地方各级人民政府负责特种设备安全监督管理的部门对下级行政区域内种设备安全实施监督管理。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '3、国家对特种设备的生产、经营一使用，实施分类的、全过程的安全监督管理。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '4、特种设备生产、经营、使用、检验、检测应当遵守有关特种设备安全技术规范及相关标准。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '5、特种设备等技术规范由国务院负责特种设备安全监督管理的门委托各级地方政府制定。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '6、特种设备行业协会应当加强行业自律，推进行业诚信体系建设，提高特种设备安全管理水平。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '7、特种设备生产、经营、使用单位及其分管特种设备的负责人对其生产、经营、使用的特种设备安全负责。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '8、特种设备生产、经营、使用单位应当按照国家有关规定配备特种设备安全管理人员、检测人员和作业人员，有条件的可以开展安全教育和技能培训。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '9、特种设备交全管理人员、检测人员和作业人员经使用单位培训育方成从事相关工作。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '10、特种设备安全管理人员、检测人员和作业人员应当严格执行安全我术规范和管理制度，保证特种设备安全。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '11、特种设备生产、经营、使用单位对其生产、经营、使用的特种设备应当进行自行检测和维护保养，对国家规定实行检验的特种设备应当及时申报并接受检验。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '12、特种设备采用新材料、新技术、新工艺，与安全技术规范的要求不一致，经技术负责人批准，可投入生产、使用。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '13、国家强制投保特种设备安全责任保险。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '14、锅炉、气瓶、氧舱、客运索道、大型游乐设施的设计文件，应当经负责特种设备安全监督管理的部门核准的检验机构鉴定，方可用于制造。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '15、电梯的维护保养，必须由电梯制造单位或者其委托的依照本法取得相应许可的单位进行。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '16、电梯制造单位对电梯安全性能负责。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '17、特种设备安装、改造、修理的施工单位应当在施工前或施工后30日内将拟进行的特种设备安装、改造、修理情况书面告知直辖市或者设区的市级人民政府负责特种设备安全监督管理的部门。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '18、特种设备安装、改造、修理竣工后，安装、改造、修理的施工单位应当在验收后六十日内将相关技术资料和文件移交特种设备使用单位。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '19、锅炉、压力容器、压力管道元件等特种设备的制造过程应当经特种设备检验机构按安全技术规范的要求进行监督检验。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '20、锅炉、压力容器、压力管道、电梯、起重机械、客运案道、大型游乐设施的安装、改造、重大修理过程，应当经特种设备检验机构按照安全技术规道的要求进行监督检验。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '21、未经监督检验或者监督检验不合格的特种设备，不得出厂或者交付使用。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '22、国家建立缺陷特种设备召回制度。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '23、因生产原因造成特种设备存在危及安全的同一性缺陷的，特种设备生产单位应当查找原因，修复解决。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '24、国务院负责特种设备安全监督管理的部门发现特种设备存在应当召回而未召回的情形时，应当责令特种设备生产单位停止生产。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '25、进口的特种设备应当将符合出口国安全技术规范的要求、并经检验合格。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '26、进口特种设备其发装及使用维护保养说明、产品铭牌、安全警示标志及其说明应当采用生产国的文字。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '27、特制设备销售单位应当建立特种设备检查验收和销售记录制度。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '28、特种设备销售单位销售的特种设备，应当符合安全技术规范及相关标准的要求，其设计文件、产品质量合格证明、安装及使用维护保养说明、监督检验证明等相关技术资料和文件应当齐全。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '29、特种设备出租单位不得出租未取得许可生产的特种设备或者国家明令淘汰和已经报废的特种设备。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '30、特种设备出租单位不得出租未按照安全技术规范的要求进行维护保养和未经检验或者检验不合格的特种设备。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '31、特种设备使用单位可以使用取得许可生产并经自检合格的特种设备。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '32、特种设的使用单位必须在特种设备入使用前三十日内，向负责特种设备安全监督管理的部门办理使用登记。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '33、特种设备使用单位应当按照丝安全技术规范的要求，在检验合格有效期届满前15日内向特种设备检验机构提出定期检验要求。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '34、特种设备使用单位应当对其使用的特种设备的安全附件、安全保护装置进行自查并作出记录。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '35、特种设备的使用应当具有规定的安全距离、安全防护措施。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '36、特种设备出现故障或者发生异常情况，特种设备使用单位应当对其进行全面检查，消除事故隐患，方可继续使用。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '37、电梯、客运索道、大型游乐设施的运营使用单位应当将电梯、客运索道、大型游乐设施的安全使用说明、安全注意事项和警示标志置于易于为乘客注意的显著位置。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '38、锅炉使用单位应当接照安全技术规范的要求进行锅炉水（介）质处理，自行检测合格即可。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '39、电梯的维护保养应当由电梯制造单位或者依照本法取得许可的安装、改造、修理单位进行。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '40、电梯的维护保养单位应当对其维护保养的电梯的产品质量负责。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '41、电梯投入使用后，电梯制造单位应当对其制造的电梯的安全运行情况进行跟踪调查和了解，并作出记录。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '42、大型游乐设施每十五日开展一次试运行和安全检查，对安全附件和安全保护装置进行检查确认。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '43、特种设备检验、检测机构的检验、检测人员可以同时在两个以上检验、检测机构中执业。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '44、特种设备检验、检测机构及其检验、检测人员应当依法为特种设备生产、经营、使用单位提供安全、可靠、便捷、诚信的检验检测服务。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '45、特种设备检验检测机构征得主管单位同意可以从事特种设备特种设备生产、经营活动。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '46、特种设备验、检测机构及其检验、检测人员对检验、检测过域程中知悉的商业秘密，负有保密义务。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '47、特种设备检经检测人员不得以本单位名义监制特种设备。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '48、特种设备使用单位应当制定特种设备事故应急专项预案，并定期进行应急演练。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '49、特种设备安全法自2014年1月1日起施。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '50、特种设备发生重大事故，由国务院或者国务院授权有关部门组织故调查组进行调查。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '51、事故调查组应当依法、独立、公正开展调查，提出事故调查报告。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '52、特种设备发生较大事故，由设区的市级人民政府负责特种设备安全监冬管理的部门会同有关部门组织事故调查组进行调查。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '53、未经许可从事特种设备生产活动的，责令停止生产，没收违法制造的特种设备，处三十万元以上五十万元以下罚款。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '54、未经许可从事特种设备生产活动已经实施安装、改造、修理的，责令恢复原状或者责令限期由取得许可的单位重新安装、改造、修理。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '55、特种设备未进行型式试验的，处三万元以上三十万元以下罚款。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '56、特种设备安装、改造、修理的施工单位在施工前未书面告知负责特种设备安全监督管理的部门即行施工的，责令限期改正；逾期未改正的，处一万元以上十万元以下罚款。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '57、特种设备安装、改造、修理的施工单位在验收后三十日内未将相关技术资料和文件移交特种设备使用单位的，责令限期改正；逾期未改正的，处一万元以上十万元以下罚款。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '58、特种设备的制造、安装、改造、重大修理过程，未经监督检验的，处五万元以上二十万元以下罚款。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '59、锅炉清洗过程，未经监督检验的，处五万元以上二十万元以下罚款。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '60、使用未取得许可生产，未经检验或者检验不合格的特种设备，或者国家明令淘汰、已经报废的特种设备的，责令停止使用有关特种设备，处三万元以上三十万元以下罚款。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '61、特种设备出现故障或者发生异常情况，未对其进行全面检查、消除事故隐患，继续使用的，责令停止使用有关特种设备，处三万元以上三十万元以下罚款。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '62、特种设备存在严重事故隐患，无改造、修理价值，或者达到安全技术规范规定的其他报废条件，未依法履行报废义务，并办理使用登记证书注销手续的，责令停止使用有关特种设备，处三万元以上三十万元以下罚款。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '63、移动式压力容器、气瓶充装单位对不符合安全技术规范要求的移动式压力容器和气瓶进行充装的，责令改正，处二万元以上二十万元以下罚款；情节严重的，吊销充装许可证。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '64、特种设备生产、经营、使用单位未对特种设备安全管理人员、检测人员和作业人员进行安全教育和技能培训的责令限期改正；逾期未改正的，责令停止使用有关特种设备或者停产停业整顿，处一万元以上五万元以下罚款。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '65、乡（镇）级以上地方各级人民政府及其负责特种设备安全监督管理的部门应当依法组织制定本行政区域内特种设备事故应总预案，建立或者纳入相应的应急处置与救援体系。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '66、负责特种设备安全监督管理的部门实施安全监督检查时，应当有三名以上特种设备安全监察人员参加，并出示有效的特种设备安全行政执法证件。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '67、客运索道、大型游乐设施每日投入使用前，未进行试运行和例行安全检查，未对安全附件和安全保护装置进行检查确认的责令停止使用有关特种设备或者停产停业整顿，处二万元以上十万元以下罚款。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '68、未经许可，擅自从事电梯维护保养的，责令停止违法行为，处五万元以上十万元以下罚款；有违法所得的，没收违法所得。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '69、特种设备安全管理人员、检测人员和作业人员应当按照国家有关规定进行培训，方可从事相关工作。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '70、电梯制造单位委托其他单位进行电梯安装、改造、修理的，应当对其安装、改修理进行安全指导和监控，并按照安全技术规范的要求进行校验和调试。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '71、电梯使用单位安全管理人员应当对电梯安装、改造、维修和维保工作实施监督，维保单位的维保记录签字确认。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '72、电梯使用管理者（指具有电梯管理权利和义务的单位或个人)按照“谁所有谁负责、谁共有谁负责、谁主管谁负责”的原则确定。未落实电梯使用管理者的电梯，不得投入使用。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '73、《特种设备安全监察条例》2003年3月11日中华人民共和国国务院令第373号公布；根据2009年1月24日《国务院关于修改<特种设备安全监察条例>的决定》修订，2009年5月1日起施行。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '74、《特种设备安全监察条例》所称特种设备是指涉及生命安全、危险性较大的锅炉、压力容器含气瓶、压力管道、电梯、起重机械、客运索道、大型游乐设施和场厂内专用机车辆。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '75、市政工程工地使用的起重机械、场内机动车辆等按照《特种设备安全监察条例》的规定需要接受监督管理。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '76、特种设备包括其所用的材料、附属的安全附件、安全保护装置和与安全保护装置相关的设施。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '77、特种设备不符合能效指标的，特种设备使用单位应当采取相应措施进行整改。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '78、特种设备的改造、维修竣工后，施工单位应当在验收后30日内将有关技术资料移交使用单位。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '79、特种设备使用单位可自行对其单位在用的特种设备进行安装、改造、维修。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '80、特种设备使用单位应当至少每月进行一次自行检查，并作出记录。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '81、特种设备在投入使用前或投入使用后20日内，特种设备使用单位应当向直辖市或者设区的市的特种设备全监督管理部门登记。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '82、移动式压力容器充装单位应当经所在地的特种设备安全监察管理部门许可，方可从事充装活动。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '83、600兆瓦以上锅炉爆炸或压力容器、压力管道有毒介质泄漏，造成15万人以上转移的特种设备事故属于特别重大事故。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '84、根据《特种设备安全监察条例》的规定，电梯应当至少每15日进行一次清洁、润滑、调整和检查。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '85、按照《特种设备安全监察条例》的规定，特种设备事故分为特别重大事故、重大事故、较大事故和普通事故。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '86、按照《特种设备安全监察条例》的规定，特种设备事故分为特别重大事故、重大事故、较大事故和一般事故。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '87、按照《特种设备安全监察条例》的规定，特种设备事故分为特别重大事故、重大事故和一般事故。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '88、发生特种设备重大事故及其以下等级事故的，依照《特种设备安全监察条例》的有关规定实施行政处罚和处分；构成犯罪的，依法追究刑事责任。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '89、国务院令第549号《国务院关于修改<特种设备安全监察条例>的决定》已经2009年1月14日国务院第46次常务会议通过，现予公布，自2009年1月14日起施行。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '90、国务院令第549号《国务院关于修改<特种设备安全监察条例>的决定》已经2009年1月14日国务院第46次常务会议通过，现予公布，自2009年5月1日起施行。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '91、矿山井下使用的特种设备的安全监察适用《特种设备安全监察条例》。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '92、特种设备使用单位未依照《特种设备安全监察条例》第二十六条的规定，建立特种设备安全技术档案的，由特种设备安全监督管理部门责令限期改正；逾期未改正的，处5000元以上5万元以下罚款：情节严重的，责令停止使用或者停产停业整顿。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '93、特种设备使用单位未依照《特种设备安全监察条例》第二十七条的规定，对在用特种设备进行经常性日常维护保养和定期自行检查的，或者对在用特种设备的安全附件、安全保护装置、测量调控装置及有关附属仪器仪表进行定期校验、检修，并作出记录的，由特种设备安全监督管理部门责令限期改正；逾期未改正的，处1万元以上5万元以下罚款；情节严重的，责令停止使用或者停产停业整顿。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '94、特种设备使用单位未依照《特种设备安全监察条例》第二十七条的规定，对在用特种设备安全监督管理部门责令限期改正；逾期未改正的，处5000元以上5万元以下罚款：情节严重的，责令停止使用或者停产停业整顿。 ', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '95、特种设备使用单位未依照《特种设备安全监察条例》规定设置特种设备安全管理机构或者配备专职、兼职的安全管理人员的，由特种设备安全监督管理部门责令限期改正：逾期未改正的，责令停止使用或者停产停业整顿，处100元以上5000元以下罚款。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '96、发生特种设备事故，伪造或者故意破坏事故现场的，构成犯罪的，依法追究刑事责任；构成有关法律法规规定的违法行为的，依法予以行政处罚；未构成有关法律法规规定的违法行为的，由质量技术监督部门等处以5000元以上3万元以下的罚款。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '97、发生特种设备事故，阻挠、干涉特种设备事故报告和调查处理工作的，构成犯罪的，依法追究刑事责任；构成有关法律法规规定的违法行为的，依法予以行政处罚；未构成有关法律法规规定的违法行为的，由质量技术监督部门等处以4000元以上2万元以下的罚款。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '98、发生特种设备事故后，情况紧急时，事故现场有关人员可以直接向事故发生地的县以上质量技术监督部门报告。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '99、发生特种设备事故后，事故现场有关人员应当立即向事故发生单位负责人报告：事故发生单位的负责人接到报告后，应当于12小时内向事故发生地的县以上质量技术监督部门和有关部门报告。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '100、发生特种设备事故后，因抢救人员、防止事故扩大以及疏通交通等原因，需要移动事故现场物件的，负责移动的单位或者相关人员应当做出标志，绘制现场简图并做出书面配录，妥善保存现场重要痕迹、物证。有条件的，应当现场制作视听资料。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '101、特种设备出现故障或者发生异常情况，使用单位应当对其进行局部检查，消除事故隐患后，方可重新投入使用。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '102、特种设备及其安全附件、安全保护装置、测量调控装置及有关附属仪器仪表的日常维护保养记录是特种设备安全技术档案应当包括的内容之一。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '103、特种设备生产、使用单位的安全管理人员应当对本单位特种设备的安全和节能全面负责。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '104、特种设备生产、使用单位和特种设备检验检测机构，应当保证必要的安全和节能投入。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '105、特种设备生产、使用单位和特种设备检验检测机构，应当接受特种设备安全监督管理部门依法进行的特种设备安全监察。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '106、特种设备使用单位应当按照安全技术规范的定期检验要求，在安全检验合格有效期届满前半个月向特种设备检验检测机构提出定期检验要求。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '107、特种设备使用单位应当建立特种设备安全技术档案。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '108、特种设备使用单位应当制定事故应急专项预案，并经常进行事故应急演练。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '109、特种设备使用单位未依照《特种设备安全监察条例》第二十六条的规定，建立特种设备安全技术档案的，由特种设备安全监督管理部门责令限期改正：逾期未改正的，处2000元以上2万元以下罚款：情节严重的，责令停止使用或者停产停业整顿。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '110、特种设备作业人员未取得《特种设备作业人员证》上岗作业，或者特种设备生产、使用单位未对特种设备作业人员进行安全教育和培训，按照《特种设备安全监察条例》第七十七条的规定对特种设备生产、使用单位予以处罚。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '111、发生较大事故的，对事故发生负有责任的单位，由特种设备安全监督管理部门处30万元以上60万元以下罚款。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '112、对特种设备作业人员进行考核时，应当按照有关特种设备安全技术规范的规定，将节能管理知识和节能操作技能纳入高耗能特种设备的作业人员考核内容。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '113、本省行政区域内特种设备的生产（含设计、制造、安装、改造、维修）、销售、使用、检验、检测和监督管理，应当遵守省安全条例。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '114、特种设备，是指涉及生命安全、危险性较大的锅炉、压力容器（含气瓶，下同）、压力管道、电梯、起重机械、客运索道、大型游乐设施和场（厂）内专用机动车辆，不包括特种设备附属的安全附件、安全保护装置和与安全保护装置相关的设施。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '115、市级以上人民政府应当加强对本行政区域内特种设备安全工作的领导，建立健全特种设备安全监督管理联席会议制度，及时协调、解决特种设备安全监督管理中存在的重大问题，督促各有关部门依法履行职责，保障特种设备安全监督管理必需的经费，并将特种设备安全工作纳入对下级人民政府的考核内容。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '116、市级以上质量技术监督部门是负责特种设备安全监督管理的部门，负责本行政区域内特种设备安全监督管理工作，', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '117、特种设备的生产单位，应当依法报经国务院或者省特种设备安全监督管理部门许可，', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '118、特种设备作业人员和检验、检测人员，应当按照国家有关规定经特种设备安全监督管理部门考核合格，取得特种设备作业人员证书或者检验、检测人员证书。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '119、特种设备的销售者应当建立并执行进货检查验收制度，验明特种设备出厂时应当附有的符合安全技术规范要求的设计文件、产品质量检验合格证明、安装及使用维修说明、监督检验证明等文件。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '120、特种设备的生产单位为管理责任人。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '121、使用单位委托其他管理人管理特种设备的，使用单位为管理责任人。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '122、新安装特种设备未移交使用单位的，项目建设单位为管理责任人。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '123、未确定管理责任人的，电梯不得投入使用；已投入使用的，应当暂停使用。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '124、锅炉用水的水质、锅炉的化学清洗和停炉保养，应当符合安全技术规范的要求', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '125、锅炉停用两年以上重新启用的，应当经检验、检测机构检验、检测合格。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '126、电梯的日常维护保养，应当由电梯制造单位或者依法取得许可的安装、改造、修理单位按照安全技术规范和使用维护保养说明的要求进行。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '127、鼓励特种设备制造单位直接从事本单位所制选特种设备的日常维护保养活动。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '128、电梯主要部件严重损坏，危及公共安全的，可以按照设区的市规定的简易程序申请使用物业专项淮修资金。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '129、特种设备使用单位应当在三十日内将法定的检验、检测结果和鉴定结论，报县（市、区）特种设备安全监督管理部门备案。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '130、禁止生产、销售和使用用油桶等容器改装或者采用其他类似材质卷制、焊接的可以输出蒸汽、产生压力的简易设备。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '131、特种设备安全监督管理部门可以根据事故影响程度，委托下一级特种设备安全监督管理部门会同同级有关部门组织事故调查组进行事故原因调查；受委托的特种设备安全监督管理部门也可再委托其他部门组织事故调查组进行事故原因调查。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '132、特种设备安全监督管理部门及其工作人员在特种设备安全监督管理工作中玩忽职守、衔私舞弊、滥用职权的，依法追究行政、刑事等法律责任、', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '133、简易设备构成特种设备的，依照《中华人民共和国特种设备安全法》的规定处罚。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '134、为加强高耗能特种设备节能审查和监管，提高能源利用效率，促进节能降耗，根据《中华人民共和国节约能源法》、《特种设备安全监察条例》等法律、行政法规的规定，制定本办法。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '135、本办法所称高耗能特种设备，是指在使用过程中能源消耗量或者转换量大，并具有较大节能空间的锅炉、换热压力容器、起重机等特种设备。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '136、为加强高耗能特种设备节能审查，提高能源利用能力，促进节能降耗，根据《中华人民共和国节约能源法》、《特种设备安全监察条例》等法律、行政法规的规定，制定本办法。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '137、本办法所称高耗能特种设备，是指在使用过程中能源消耗量或者转换量大，并具有较大节能空间的锅炉、换热压力容器、电梯等特种设备。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '138、高耗能特种设备生产（含设计、制造、安装、改造、维修，下同）、使用的节能监督管理，适用本办法。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '139、高耗能特种设备生产（含设计、制造、安装、改造、维修，下同）、使用、检验检测的节能监督管理，适用本办法。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '140、地方各级质量技术监督部门负责本行政区城内高耗能特种设备的节能监督管理工作。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '141、高耗能特种设备节能监督管理实行安全监察与监督检查相结合的工作机制。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '142、高耗能特种设备节能监督管理实行安全监察与节能监管相结合的工作机制。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '143、高耗能特种设备的生产单位、使用单位应当按照国家有关法律、法规、特种设备安全技术规范等有关规范和标准的要求，履行节能义务，做好高耗能特种设备节能工作，并接受国家质检总局和地方各级质量技术监督部门的监督检查。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '144、高耗能特种设备的生产单位、使用单位、检验检测机构应当按照国家有关法律、法规、特种设备安全技术规范等有关规范和标准的要求，履行节能义务，做好高耗能特种设备节能工作，并接受国家质检总局和地方各级质量技术监督部门的监督检查。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '145、国家鼓励高耗能特种设备的生产单位、使用单位应用新技术、新工艺、新产品，提高特种设备能效水平。对取得显著成绩的单位和个人，按照有关规定予以奖励。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '146、高耗能特种设备生产单位应当按照国家有关法律、法规、特种设备安全技术规范等有关规范和标准的要求进行生产，确保生产的高耗能特种设备符合能效指标要类。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '147、高耗能特种设备生产单位应当按照国家有关法律、法规、特种设备安全技术规范等有关规范和标准的要求进行生产，确保生产的高耗能特种设备符合节能指标要求。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '148、特种设备生产单位不得生产不符合能效指标要求或者国家产业政策明令淘汰的高耗能特种设备。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '149、特种设备使用单位不得使用不符合能效指标要求或者国家产业政策明令淘汰的高耗能特种设备。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '150、高耗能特种设备的设计，应当在设备结构、系统设计、材料选用、工艺制定、计量与监控装置配备等方面符合有关技术规范和标准的节能要求。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '151、高耗能特种设备的设计、生产，应当在设备结构、系统设计、材料选用、工艺制定、计量与监控装置配备等方面符合有关技术规范和标准的节能要求。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '152、高耗能特种设备的设计文件，应当经安全监察机构，按照有关特种设备安全技术规范和标准的规定进行鉴定，方可用于制造。未经鉴定或者鉴定不合格的，制造单位不得进行产品制造。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '153、高耗能特种设备制造企业的新产品不需要进行能效测试。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '154、高耗能特种设备制造企业的新产品应当进行能效测试。未经能效测试或者测试结果未达到能效指标要求的，不得进行批量制造。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '155、锅炉、换热压力容器产品在试制时进行能效测试。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '156、电梯产品在安全性能型式试验时进行能效测试。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '157、特种设备检验检测机构接到高耗能特种设备制造单位的产品能效测试申请，应当按照有关特种设备安全技术规范和标准的要求进行测试，并出具能效测试报告。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '158、高耗能特种设备的安装、改造、维修，允许降低产品及其系统的原有能效指标。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '159、特种设备检验检测机构发现设备和系统能效项目不符合相关特种设备安全技术规范要求时，应当及时告知高耗能特种设备安装、改造、维修单位。被告知单位应当依照特种设备安全技术规范要求进行评估或者能效测试，符合要求后方可交付使用。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '160、特种设备检验检测机构发现设备和系统能效项目不符合相关特种设备安全技术规范要求时，应当及时告知当地安全监察机构。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '161、高耗能特种设备安装、改造、维修单位应当向使用单位移交有关节能技术资料。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '162、高耗能特种设备使用单位应当严格执行有关法律、法规、特种设备安全技术规范和标准的要求，确保设备及其相关系统安全、经济运行。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '163、高耗能特种设备安装、生产、使用单位应当建立健全经济运行、能效计量监控与统计、能效考核等节能管理制度和岗位责任制度。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '164、高耗能特种设备使用单位办理特种设备使用登记时，应当按照有关特种设备安全技术规粒的要求，提供有关能效证明文件。对国家明令淘汰的高耗能特种设备，不予办理使用登记。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '165、对特种设备作业人员进行考核时，应当按照有关特种设备安全技术规范的规定，将节能管理知识和节能操作技能纳入高托能特种设备的作业人员考核内容。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '166、锅炉清洗的单位应当按照有关特种设备安金技术规范的要求对锅炉进行清洗，接受特种设备检验检测机构实施的锅炉清洗过程监督检验，保证锅炉消洗工作安全有效进行。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '167、锅炉清洗单位应当按照有关特种设备安全技术规范的要求对锅炉进行清洗，只要确保锅炉清洗工作安全，可以不向特种设备检验检测机构申请监督检验。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '168、高耗能特种设备使用单位应当开展节能教育和培训，提高作业人员的节能意识和操作水平，确保特种设备安全、经济运行。高耗能特种设备的作业人员应当严格执行操作规程和节能管理制度。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '169、管理知识和节能操作技能纳入高耗能特种设备的作业人员考核内容。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '179、特种设备检验检测机构在特种设备定期检验时，应当按照特种设备安全技术规范和标准的要求对设备安全性能进行检查，不需要对设备的能效状况进行检查。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '171、高耗能特种设备及其系统的运行能效不符合特种设备安全技术规范等有关规范和标准要求的，使用单位应当分析原因，采取有效措施，实施整改或者节能改造。整改或者改造后仍不符合能效指标要求的，不得继续使用。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '172、对在用国家明令海汰的高耗能特种设备，使用单位应当在规定的期限内予以改造或者更换。到期未改造或者更换的，不得继续使用。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '173、各级市场监督管理部门发现高耗能特种设备生产单位、使用单位和检验检测机构违反有关法律、法规、特种设备安全技术规范和标准的行为，应当以书面形式责令有关单位予以改正。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '174、国家市场监管总局和省、自治区、直辖市市场监管部门应当定期向社会公布高耗能特种设备能效状况。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '175、从事高耗能特种设备能效测试的检验检测机构，应当保证能效测试结果的准确性、对测试结果负责。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '176、从事商耗能特种设备能效测试的检验检测机构，发现在用高耗能特种设备能耗严重超标的，应当及时告知使用单位，并报告所在地的特种设备安全监督管理部门。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '177、高耗能特种设备的生产、使用、检验检测活动违反本办法规定的，依照《中华人民共和国节约能源法》、《特种设备安全监察条例》等相关法律法规的规定进行处罚和处分。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '178、高能耗特种设备节能监督管理办法自2009年10月1日起施行。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '179、高能耗特种设备节能监督管理办法自2008年10月1日起施行。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '180、对在用国家明令淘汰的高耗能特种设备，使用单位应当立即淘汰。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '181、高耗能特种设备安全技术档案包括日常运行能效监控记录、能耗状况记录。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '182、特种设备检验检测机构在特种设备定期检验时，应当按照特种设备安全技术规范和标准的要求对高耗能特种设备使用单位的节能管理和设备的能效状况进行检查，发现不符合特种设备安全技术规范和标准要求的，应当要求使用单位进行整改。当检查结果异常或者偏离设计参数难以判断设备运行效率时，应当由从事高耗能特种设备能效测试的检验检测机构进行能效测试，以准确评价其能效状况。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '183、高耗能特种设备节能产品推广目录、淘汰产品目录，依照《中华人民共和国节约能源法》制定并公布。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '1、本法的立法宗旨，下列哪些内容正确? （）①预防特种设备事故 ②保障人身和财产全 ③促进经济社会发展 ④加强特种设备安全工作', '**A、①②③**B、①②③④**C、①③④', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '2、下列哪项特种设备的环节适用本法? （）。①设计、制造、安装、改造、修理 ②经营、使用、检验、检测 ③监督管理 ④审查制度', '**A、①②③④**B、①②③**C、②③④', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '3、特种设备安全工作应当坚持（）、综合治理的原则。①安全第一 ②预防为主 ③节能环保 ④监督管理', '**A、②③④**B、①②③**C、①②③④', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '4、国家对特种设备的生产、经营、使用，实施分类、全过程的（）管理。', '**A、安全监督**B、设计**C、检验', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '5、国务院负责特种设备安全监督管理的部门对全国特种设备安全实施监督管理。（）以上地方各级人民政府负责特种设备安全监督管理的部门对本行政区域内特种设备安全实施监督管理。', '**A、市级**B县级**C、地区级', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '6、国务院和地方各级人民政府应当加强对特种设备安全工作的领导，（）各有关部门依法履行监督管理职责。', '**A、检查**B引导**C、督促', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '7、特种设备生产、经营、使用、检验、检测应当遵守有关（）及相关标准。', '**A、企业安全方针**B、企业质量体系**C、特种设备安全技术规范', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '8、特种设备行业协会应当（），提高特种设备安全管理水平。①加强行业自律 ②推进行业诚信体系建设 ③加强安全生产', '**A、①②**B、①③**C、②③', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '9、国家支持有关特种设备安全的科学技术研究，鼓励先进技术和先进管理方法的推广应用，对做出突出贡献的（）给予奖励。', '**A、企业领导**B、作业人员**C、单位和个人', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '10、负责特种设备安全监督管理的部门应当加强（），普及特种设备安全知识，增强社会公众的特种设备安全意识。', '**A、特种设备安全宣传教育**B、法律法规教育**C、安全生产教育', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '11、（）有权向负责特种设备安全监督管理的部门和有关部门举报涉及特种设备安全的违法行为，接到举报的部门应当及时处理。', '**A、上级主管部门**B、任何单位和个人**C、企业特种设备安全管理负责人', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '12、特种设备生产、经营、使用单位及其主要负责人对其（）的特种设备安全负责。', '**A、设备安全管理**B、检验、使用**C、生产、经营、使用', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '13、特种设备生产、经营、使用单位对其（）的特种设备应当进行（）和维护保养，对国家规定实行检验的特种设备应当（）并接受检验。①生产、经营、使用 ②自行检测 ③及时申报 ④及时维护', '**A、②③④**B、①②③④**C、①②③', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '14、特种设备采用（），与安全技术规范的要求不一致，或者安全技术规范未作要求、可能对安全性能有重大影响的，应当向国务院负责特种设备安全监督管理的部门中报，由国务院负责特种设备安全监督管理的部门及时委托安全技术咨询机构或者相关专业机构进行技术评审，评审结果经国务院负责特种设备安全监督管理的部门批准，为可投入生产、使用。', '**A、新设计、新型号、新材料**B、新材料、新技术、新工艺**C、新规范、新理论、新技术', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '15、国家（）投保特种设备安全责任保险。', '**A、鼓励**B、禁止**C、限制', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '16、国家按照分类监督管理的原则对特种设备生产实行许可制度。特种设备生产单位应当具备下列（）条件，并经负责特种设备安全监督管理的部门许可，方可从事生产活动：①有与生产相适应的专业技术人员；②有与生产相适应的设备、设施和工作场所；③有健全的质量保证、安全管理和岗位责任等制度', '**A、②③**B、①②**C、①②③', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '17、特种设备生产单位应当保证特种设备生产符合安全技术规范及相关标准的要求，对其生产的特种设备的安全性能负责。不得生产不符合（）以及国家明令淘汰的特种设备。①安全性能要求 ②能效指标 ③技术要求', '**A、①③**B、①②**C、①②③', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '18、锅炉、气瓶、氧舱、（）、大型游乐设施的设计文件，应当经负责特种设备安全监督管理的部门核准的检验机构鉴定，方可用于制造。', '**A、客运索道**B、电梯**C、起重机械', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '19、电梯的（）、必须由电梯制造单位或者其委托的依照本法取得相应许可的单位进行。电梯制造单位委托其他单位进行电梯安装、改造、修理的，应当对其安装、改造、修理进行安全指导和监控，并按照安全技术规范的要求进行校验和调试。电梯制造单位对电梯安全性能负责。', '**A、设计、制造、维修**B、安装、改造、修理**C、生产、调试、安装', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '20、特种设备安装、改造、修理的施工单位应当在施工前将拟进行的特种设备安装、改造、修理情况书面告知（）或者设区的市级人民政府负责特种设备安全监督管理的部门。', '**A、直辖市**B、省级**C、县级', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '21、特种设备安装、改造、修理竣工后，安装、改造、修理的施工单位应当在验收后（）内将相关技术资料和文件移交特种设备使用单位。特种设备使用单位应当将其存入该特种设备的安全技术档案。', '**A、十日**B、二十日**C、三十日', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '22、锅炉、压力容器、压力管道元件等特种设备的制造过程和锅炉、压力容器、压力管道、（）、起重机械、客运索道、大型游乐设施的安装、改造、重大修理过程，应当经特种设备检验机构按照安全技术规范的要求进行监督检验；未经监督检验或者监督检验不合格的，不得出厂或者交付使用。', '**A、核设施**B、电梯**C、场（厂）内专用机动车辆', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '23、国家建立缺陷特种设备召回制度。因生产原因造成特种设备存在危及安全的同一性缺陷的，特种设备生产单位应当（），主动召回。', '**A、立即停止生产**B、报主管部门**C、停业整顿', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '24、特种设备销售单位销售的特种设备，应当符合安全技术规范及相关标准的要求，其设计文件、（）、安装及使用维护保养说明、监督检验证明等相关技术资料和文件应当齐全。', '**A、使用说明**B、产品质量合格证明**C、制造许可证书', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '25、特种设备在出租期间的（）由特种设备出租单位承担，法律另有规定或者当事人另有约定的除外。', '**A、使用管理和维的保养义务**B、安装调试**C、日常维护', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '26、进口的种设备应当符合我国（）的要求，并经检验合格；需要取得我国特种设备生产许可的，应当取得许可。', '**A、安全技术规范**B、生产规范**C、调试合格', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '27、进口特种设备，应当向（）负责特种设备安全监督管理的部门履行提前告知义务。', '**A、进口地**B、当地**C、使用地', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '28、特种设备使用单位应当使用取得（）并经检验合格的特种设备。', '**A、许可生产**B、经营许可**C、标准认证', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '29、特种设备使用单位应当在特种设备投入使用前或者投入使用后（）内，向负责特种设备安全监督管理的部门办理使用登记，取得使用登记证书。登记标志应当置于该特种设备的显著位置。', '**A、十五日**B、二十日**C、三十日', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '30、特种设备使用单位应当建立（）等安全管理制度，制定操作规程，保证特种设备安全运行。①岗位责任 ②隐患治理 ③应急救援 ④安全生产', '**A、①②③**B、②③④**C、①②③④', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '31、电梯、客运索道、（）等为公众提供服务的特种设备的运营使用单位，应当对特种设备的使用安全负责，设置特种设备安全管理机构或者配备专职的特种设备安全管理人员；其他特种设备使用单位，应当根据情况设置特种设备安全管理机构或者配备专职、兼职的特种设备安全管理人员。', '**A、非公路旅游观光车**B、大型游乐设施**C、压力管道', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '32、特种设备的使用应当具有规定的（）、安全防护措施。', '**A、技术**B、安全距离**C、使用安全', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '33、特种设备属于（），共有人可以委托物业服务单位或者其他管理人管理特种设备，受托人履行本法规定的特种设备使用单位的义务，承担相应责任。共有人未委托的，由共有人或者实际管理人履行管理义务，承担相应责任。', '**A、共有的**B、公有的**C、私有的', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '34、特种设备使用单位应当对其使用的特种设备进行（），并作出记录。①经常性维护保养②定期自行检查 ③调试维修', '**A、①③**B、①②**C、②③', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '35、特种设备使用单位应当按照安全技术规范的要求，在检验合格有效期届满前（）向特种设备检验机构提出定期检验要求。', '**A、一年**B、一个月**C、两个月', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '36、特种设备安全管理人员应当对特种设备使用状况进行经常性检查，发现问题应当立即处理；情况紧急时，可以决定（）特种设备并及时报告本单位有关负责人。', '**A、停止使用**B、现场修理**C、监护运行', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '37、（）大型游乐设施在每日投入使用前，其运营使用单位应当进行试运行和例行安全检查，并对安全附件和安全保护装置进行检查确认。', '**A、客运素道**B、锅炉**C、电梯', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '38、锅炉使用单位应当按照安全技术规范的要求进行锅炉水（介）质处理，并接受（）的定期检验。', '**A、安管人员**B、特种设备检验机构**C、维保单位', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '39、电梯的维护保养应当由（）或者依照本法取得许可的安装、改造、修理单位进行。', '**A、电梯制造单位**B、电梯使用单位**C上级主管部门', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '40、电梯投入使用后，电梯制造单位应当对其制造的电梯的安全运行情况进（），对电梯的维护保养单位或者使用单位在维护保养和安全运行方面存在的问题，提出改进建议，并提供必要的技术帮助；发现电梯存在严重事故隐患时，应当及时告知电梯使用单位，并向负责特种设备安全监督管理的部门报告。电梯制造单位对调查和了解的情况，应当作出记录。', '**A、定期检查**B、跟踪调查和了解**C、调试维修', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '41、特种设备进行改造、修理，按照规定需要变更使用登记的，（）方可继续使用。', '**A、应当办理变更登记**B、更新登记**C、撤销登记', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '42、特种设备存在（），无改造、修理价值，或者达到安全技术规范规定的其他报废条件的，特种设备使用单位应当依法履行报废义务，采取必要措施消除该特种设备的使用功能，并向原登记的负责特种设备安全监督管理的部门办理使用登记证书注销手续。', '**A、过期使用**B、检测不合格**C、严重事故隐患', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '43、从事本法规定的监督检验、定期检验的特种设备检验机构，以及为特种设备生产、经营、使用提供检测服务的特种设备检测机构，应当具备下列哪些条件，并经负责特种设备安全监督管理的部门核准，方可从事检验、检测工作。（）①有与检验、检测工作相适应的检验、检测人员；②有与检验、检测工作相适应的检验、检测仪器和设备；③有健全的检验、检测管理制度和责任制度。', '**A、①③**B、①②**C、①②③', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '44、特种设备检验、检测机构的检验、检测人员应当（），取得检验、检测人员资格，方可从事检验、检测工作。', '**A、经领导批准**B、经考核**C、经审核', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '45、特种设备检验、检测工作应当（）的规定，并按照安全技术规范的要求进行。', '**A、遵守法律、行政法规**B、执行相关标准**C、建立相关制度', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '46、特种设备检验、检测机构及其检验、检测人员应当（）地出具检验、检测报告，并对检验、检测结果和鉴定结论负责。', '**A、真实、公平、诚信**B、客观、公正、及时**C、严肃、认真、准确', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '47、特种设备生产、经营、使用单位应当按照（）的要求向特种设备检验、检测机构及其检验、检测人员提供特种设备想关资料和必要的检验、检测条件，并对资料的真实性负责。', '**A、安全技术规范**B、法律规定**C、有关技术部门', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '48、违反本法规定，负责特种设备安全监督管理的部门及其工作人员有下列行为之一的，由上级机关责令改正，对直接负责的主管人员和其他直接责任人员，依法给予处分（）：①未依照法律、行政法规规定的条件、程序实施许可的。②发现特种设备生产单位不再具备本法规定的条件而不吊销其许可证，或者发现特种设备生产、经营、使用违法行为不予查处的。③发现违反本法规定和安全技术规范要求的行为或者特种设备存在事故隐患，不立即处理的。④推荐或者监制、监销特种设备的。', '**A、①③④**B、②③④**C、①②③④', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '49、负责特种设备安全监督管理的部门依照本法规定，对特种设备生产、经营、使用单位和检验、检测机构实施（）。', '**A、定期检查**B、监督检查**C、定期抽查', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '50、负责特种设备安全监督管理的部门实施本法规定的许可工作，应当依照本法和其他有关法律、行政法规定的条件和程序以及（）的要求进行审查；不符合规定的，不得许可。', '**A、地方标准**B、技术参数**C、安全技术规范', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '51、负责特种设备安全监督管理的部门在办理本法规定的许可时，其受理、审查、许可的程序必须公开，并应当自受理申请之日起（）内，作出许可或者不予许可的决定；不予许可的，应当书面向申请人说明理由。', '**A、十五日**B、二十日**C、三十日', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '52、负责特种设备安全监督管理的部门对依法办理使用登记的特种设备应当建立完整的（）系统；对达到报废条件的特种设备，应当及时督促特种设备使用单位依法履行报废义务。①监督管理档案 ②信息查询 ③检测数据', '**A、①②**B、②③**C、①③', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '53、负责特种设备安全监督管理的部门在依法履行监督检查职责时，可以行使下列哪些职权？（）①进入现场进行检查，向特种设备生产、经营、使用单位和检验、检测机构的主要负责人和其他有关人员调查、了解有关情况；②根据举报或者取得的涉嫌违法证据，查阅、复制特种设备生产、经营、使用单位和检验、检测机构的有关合同、发票、账簿以及其他有关资料；③对有证据表明不符合安全技术规范要求或者存在严重事故隐患的特种设备实施查封、扣押；④对流入市场的达到报废条件或者已经报废的特种设备实施查封、扣押；⑤对违反本法规定的行为作出行政处罚决定。', '**A、①②③④⑤**B、②③④⑤**C、①②③④', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '54、负责特种设备安全监督管理的部门在依法履行职责过程中，发现违反本法规定和安全技术规范要求的行为或者特种设备存在事故隐患时，应当（）发出特种设备安全监察指令，责令有关单位及时采取措施予以改正或者消除事故隐患。紧急情况下要求有关单位采取紧急处置措施的，应当随后补发特种设备安全监察指令。', '**A、及时口头通知**B、以书面形式**C送达凭证', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '55、（）人民政府负责特种设备安全监督管理的部门不得要求已经依照本法规定在其他地方取得许可的特种设备生产单位重复取得许可，不得要求对已经依照本法规定在其他地方检验合格的特种设备重复进行检验。', '**A、地方各级**B、县级**C、市级', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '56、负责特种设备安全监督管理的部门的安全监察人员应当熟悉相关法律、法规，具有相应的（），取得特种设备安全行政执法证件。', '**A、专业如识**B、工作经验**C、资质证书', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '57、负责特种设备安全监督管理的部门对特种设备生产、经营、使用单位和检验、检测机构实施（）、应当对每次监督检查的内容、发现的问题及处理情况作出记录，并由参加监督检查的特种设备安全监察人员和被检查单位的有关负责人签字后归档。被检查单位的有关负责人拒绝签字的，特种设备安全监察人员应当将情况记录在案。', '**A、定期检查**B、问询制度**C、监督检查', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '58、负责特种设备安全监督管理的部门及其工作人员不得推荐或者监制、监销特种设备；对履行职责过程中知悉的商业秘密负有（）', '**A、法律责任**B、保密义务**C、监督义务', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '59、（）负责特种设备安全监督管理的部门和省、自治区、直辖市人民政府负责特种设备安全监督管理的部门应当定期向社会公布特种设备安全总体状况。', '**A、国家安全部门**B、国务院**C、中共中央', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '60、国家对特种设备实行（）管理。特种设备目录由国务院负责特种设备安全监督管理的部门制定，报国务院批准后执行。', '**A、目录**B、备案**C、分类', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '61、特种设备安全技术规范由（）负责特种设备安全监督管理的部门制定。', '**A、国务院**B、省级政府**C、县级政府', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '62、特种设备生产、经营、使用单位应当按照国家有关规定配备特种设备安全管理人员、检测人员和作业人员，并对其进行必要的（）和技能培训。', '**A、文化考试**B、安全教育**C、技能教育', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '63、（）负责特种设备安全监督管理的部门应当将允许使用的新材料、新技术、新工艺的有关技术要求，及时纳入安全技术规范。', '**A、地方政府**B、省级政府**C、国务院', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '64、国务院负责特种设备安全监督管理的部门发现特种设备存在应当召回而未召回的情形时，应当（）特种设备生产单位召回。', '**A、责令**B、通知**C、要求', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '65、特种设备（）单位应当建立特种设备检查验收和销售记录制度。', '**A、生产**B、使用**C、销售', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '66、进口特种设备随附的技术资料和文件应当符合规定，其安装及使用维护保养说明、产品铭牌、安全警示标志及其应当采用（）', '**A、中文**B、英文**C、中文或英文', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '67、特种设备使明单位应当对其使用的特种设备的（）、安会保护装置进行定期校验、检修，并作记录。', '**A、主要部件**B安全附件**C、辅助设施', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '68、特种设备检验机构接到定期检验要求后，应当按照（）规范的要求及时进行安全性能检验。特种设备使用单位应当将定期检验标志置于该特种设备的显著位置。', '**A、技术性能**B、安全技术**C、标准', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '69、特种设备作业人员在作业过程中发现事故隐患或者其他（），应当立即向特种设备安全管理人员和单位有关负责人报告；特种设备运行不正常时，特种设备作业人员应当按照操作规程采取有效措施保证安全。', '**A、产品故障**B、技术问题**C、不安全因素', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '70、电梯、客运索道、大型游乐设施的运营使用单位应当将电梯、客运索道、大型游乐设施的安全使用说明、安全注意事项和（）置手易于为乘客注意的显著位置。', '**A、使用说明**B、警示标志**C、安全须知', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '71、从事锅炉清洗，应当按照（）的要求进行，并接受特种设备检验机构的监督检验。', '**A、安全技术规范**B、清洗工作**C、操作手册', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '72、气瓶充装单位应当向气体使用者提供（）的气瓶，对气体使用者进行气瓶安全使用指导，并按照安全技术规范的要求办理气瓶使用登记，及时申报定期检验。', '**A、外观周正**B、配置齐全**C、符合安全技术规范要求', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '73、特种设备检验、检测机构及其检验、检测人员在检验、检测中发现特种设备存在（）隐患时，应当及时告知相关单位，并立即向负责特种设备安全监督管理的部门报告。', '**A、明显**B、安全**C、严重事故', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '74、特种设备检验、检测机构及其检验、检测人员（）从事有关特种设备的生产、经营活动，不得推荐或者监制、监销特种设备。', '**A、可以**B、不得**C、经批准允许', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '75、负责特种设备安全监督管理的部门应当组织对特种设备检验、检测机构的检验、检测结果和鉴定结论进行监督抽查，但应当防止（）抽查。监督抽查结果应当向社会公布。', '**A、重复**B、遗漏**C、随机', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '76、负责特种设备安全监督管理的部门应当对（）、幼儿园以及医院、车站、客运码头、商场、体育场馆、展览馆、公园等公众聚集场所的特种设备，实施重点安全监督检查。', '**A、机关**B、学校**C、厂区', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '77、特种设备安全监察人员应当（）、坚持原则、秉公执法。', '**A、开拓创新**B、清正廉洁**C、忠于职守', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '78、负责特种设备安全监督管理的部门实施安全监督检查时，应当有（）以上特种设备安全监察人员参加，并出示有效的特种设备安全行政执法证件。', '**A、一名**B、二名**C、三名', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '79、特种设备使用单位应当制定特种设备事故应急专项预案，并（）进行应急演练。', '**A、定期**B、不定期** C、每年', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '80、特种设备发事事故后，事故发生单位应当按照应急预案采取错施，组织抢救，防止事故扩大，减少人员伤亡和财产损失，保护事故现场和有关证据，并及时向事故发生地（）以上人民政府负责特种设备安全监督管理的部门和有关部门报告。', '**A、市级人**B、县级**C、省级', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '81、发生（）由国务院负责特种设备安全监督管理的部门会同有关部门组织事故调查组进行调查。', '**A、一般事故**B、较大事故**C、重大事故', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '82、组织事故调查的部门应当将事故调查报告报本级人民政府，并报（）人民政府负责特种设备安全监督管理的部门备案。', '**A、本级**B、省级**C、上一级', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '83、违反特设法规定，特种设备的设计文件未经鉴定，擅自用于制造的，责令改正，没收违法制造的特种设备，处（）罚款。', '**A、二万元以上二十万元以下**B、一万元以上十万元以下**C、五万元以上五十万元以下', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '84、违反特设法规定，未进行型式试验的，责令限期改正；逾期未改正的，处罚款（）。', '**A、三万元以上三十万元以下**B、一万元以上十万元以下**C、五万元以上五十万元以下', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '85、违反特设法规定，特种设备安装、改造、修理的施工单位在施工前未书面告知负责特种设备安全监督管理的部门即行施工的，或者在验收后（）内未将相关技术资料和文件移交特种设备使用单位的，责令限期改正；逾期未改正的，处一万元以上十万元以下罚款。', '**A、四十五日**B、六十日**C、三十日', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '86、违反特设法规定，特种设备的制造、安装、改造、重大修理以及锅炉清洗过程，未经监督检验的，责令限期改正；逾期未改正的，处罚款（）；有违法所得的，没收违法所得；情节严重的，吊销生产许可证。', '**A、二万元以上二十万元以下**B、一万元以上十万元以下**C、五万元以上二十万元以下', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '87、违反特设法规定，特种设备销售单位未建立检查验收和销售记录制度，或者进口特种设备未履行提前告知义务的，责令改正，处罚款（）。', '**A、二万元以上二十万元以下**B、一万元以上十万元以下**C、五万元以上五十万元以下', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '88、违反特设法规定，未经许可，擅自从事电梯维护保养的，责令停止违法行为，处罚款（）；有违法所得的，没收违法所得。', '**A、二万元以上二十万元以下**B、一万元以上十万元以下**C、五万元以上五十万元以下', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '89、违反特设法规定，特种设备生产、经营、使用单位或者检验、检测机构拒不接受负责特种设备安全监督管理的部门依法实施监督检查的，责令限期改正；逾期未改正的，责令停产停业整顿，处罚款（）。', '**A、二万元以上二十万元以下**B、一万元以上十万元以下**C、五元以上五十万元以下', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '90、违反特设法规定，被依法吊销许可证的，自吊销许可证之日起内，负责特种设备安全监督管理的部门（）不予受理其新的许可申请。', '**A、五年**B一年**C、三年', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '91、特种设备生产、经营、使用单位擅自动用、调换、转移、损毁被查封、扣押的特种设备或者其主要部件的，责令改正，处罚款；情节严重的，吊销生产许证，注销特种设备使用登记证书', '**A、二万元以上二十万元以下**B、五万元以上二十万元以下**C、一万元以上十万元以下', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '92、下列哪些情况使用的特种设备安全的监督管理不适用本法（）。①军事装备 ②核设施 ③航空航天器 ④游乐设施', '**A、①②③**B、②③④**C、①③④', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '93、违反特设法规定，特种设备经营单位有下列行为之一的，责令停止经营，没收违法经营的特种设备，处三万元以上三十万元以下罚款；有违法所得的，没收违法所得（）：①.销售、出租未取得许可生产，未经检验或者检验不合格的特种设备的；②.销售、出租国家明令淘汰、已经报废的特种设备，或者未按照安全技术规范的要求进行维护保养的特种设备的。③.建立特种设备检查验收和销售记录制度。④.销售的特种设备，符合安全技术规范及相关标准的要求，其设计文件、产品质量合格证明、安装及使用维护保养说明、监督检验证明等相关技术资料和文件应当齐全。', '**A、①③④**B、①②**C、①②③', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '94、违反特设法规定，特种设备使用单位有下列行为之一的，责令限期改正；逾期未改正的，责令停止使用有关特种设备，处一万元以上十万元以下罚款（）：①使用特种设备未按照规定办理使用登记的；②未建立特种设备安全技术档案或者安全技术档案不符合规定要求，或者未依法设置使用登记标志、定期检验标志的；③未按照安全技术规范的要求及时申报并接受检验的；④未按照安全技术规范的要求进行锅炉水（介）质处理的；', '**A、②③④**B、①②③④**C、①③④', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '95、违反本法规定，移动式压力容器、气瓶充装单位有下列行为之一的，责令改正，处二万元以上二十万元以下罚款；情节严重的，吊销充装许可证（）：①未按照规定实施充装前后的检查、记录制度的；②对不符合安全技术规范要求的移动式压力容器和气瓶进行充装的。③安装、改造、重大修理过程经特种设备检验机构按照安全技术规范的要求进行监督检验。④未经监督检验或者监督检验不合格的，不出厂或者交付使用。', '**A、①②**B、①②③④**C、②③④', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '96、违反本法规定，特种设备生产、经营、使用单位有下列情形之一的，责令限期改正；逾期未改正的，责令停止使用有关特种设备或者停产停业整顿，处以一万元以上五万元以下罚款（）：①未配备具有相应资格的特种设备安全管理人员、检测人员和作业人员的；②使用未取得相应资格的人员从事特种设备安全管理、检测和作业的；③未对特种设备安全管理人员、检测人员和作业人员进行安全教育和技能培训的；④有健全的检验、检测管理制度和责任制度。', '**A、②③④**B、①②③④**C、①②③', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '97、发生特种设备事故，有下列情形之一的，对单位处五万以上一万以下罚款；对主要负责人处一万元以上五万元以下罚款；主要负责人属于国家工作人员的，并依法给予处分（）：①发生特种设备事故时，不立即组织抢救或者在事故调查处理期间擅离职守或者逃匿的；②对特种设备事故迟报、谎报或者瞒报的；③特种设备使用单位为其使用的特种设备的安全附件、安全保护装置进行定期校验、检修，并作出记录；④特种设备出现故障或者发生异常情况，特种设备使用单位对其进行全面检查，消除事故隐患。', '**A、②③④**B、①②**C、①②③④', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '98、对事故发生负有责任的单位的主要负责人未依法履行职责或者负有领导责任的，依照下列规定处以罚款；属于国家工作人员的，并依法给予处分（）：①发生一般事故，处上一年年收入百分之三十的罚款。②发生较大事故，处上一年年收入百分之四十的罚款。③发生重大事故，处上一年年收入百分之六十的罚款。④发生重大事故，处上一年年收入百分之九十的罚款。', '**A、②③④**B、①②**C、①②③', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '99、特种设备检验、检测机构及其检验、检测人员有下列行为之一的，责令改正，对机构处五万元以上二十万元以下罚款，对直接负责的主管人员和其他直接责任人员处五千元以上五万元以下罚款；情节严重的，吊销机构资质和有关人员的资格（）：①未经核准或者超出核准范围、使用未取得相应资格的人员从事检验、检测的；②发现特种设备存在严重事故隐患，未及时告知相关单位，并立即向负责特种设备安全监督管理的部门报告的；③从事有关特种设备的生产、经营活动的；④利用检验工作故意刁难相关单位的。', '**A、①②③**B、①②③④**C、②③④', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '100、违反本法规定，负责特种设备安全监督管理的部门及其工作人员有下列行为之一的，由上级机关责令改正；对直接负责的主管人员和其他直接责任人员，依法给予处分（）：①未依照法律、行政法规规定的条件、程序实施许可的。②发现特种设备生产单位不再具备本法规定的条件而不吊销其许可证，或者发现特种设备生产、经营、使用违法行为不予查处的。③发现违反本法规定和安全技术规范要求的行为或者特种设备存在事故隐患，不立即处理的。④推荐或者监制、监销特种设备的。', '**A、①②③④**B、①②④**C、②③', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '101、《特种设备安全法》规定，违反本法规定，特种设备安全管理人员、检测人员和作业人员不履行岗位职责，违反操作规程和有关安全规章制度，（），吊销相关人员的资格。', '**A、造成后果的**B、造成事故的**C、造成不良影响的', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '102、《特种设备安全法》规定，特种设备安全管理人员应当对特种设备使用状况进行经常性检查，发现问题应当立即处理；情况紧急时，可以（）停止使用特种设备并及时报告本单位有关负责人。', '**A、提出**B、申请**C、决定', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '103、特种设备发生特别重大事故，由（）组织事故调查组进行调查。', '**A、市级人民政府**B、省级人民政府**C、国务院或者国务院授权有关部门', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '104、《特种设备安全法》规定，特种设备存在严重事故隐患，无改造、修理价值，或者达到安全技术规范规定的其他报废条件的，特种设备使用单位应当履行报废义务，采取必要措施消除该特种设备的使用功能，并向原登记的特种设备安全监督管理部门办理使用登记证书（）手续。', '**A、吊销**B、撤消**C、注销', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '105、特种设备安全监督管理部门对特种设备生产、使用单位和检验检测机构实施安全监察时，应当有（）以上特种设备安全监察人员参加，并出示有效的特种设备安全监察人员证件。', '**A、一名**B、两名**C、三名**D、五名', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '106、特种设备使用单位应当按照安全技术规范的定期检验要求，在安全检验合格有效期届满前（）向特种设备检验检测机构提出定期检验要求。', '**A、半个月**B、1个月**C、2个月**D、3个月', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '107、电梯制造单位未依法对电梯进行校验、调试的，由特种设备安全监督管理部门责令限期改正；逾期未改正的，予以（）。', '**A、通报批评**B、责令停业**C、吊销营业执照**D、处以5万元罚款', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '108、特种设备安全监督管理部门对有证据表明不符合安全技术规范要求的或者有其他严重事故隐患的特种设备或者其主要部件，可予以（）。', '**A、没收**B、查封或者扣押**C、拍卖**D、变卖', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '109、特种设备安全监督管理部门对特种设备生产、使用单位和检验检测机构进行安全监察时，发现有违反安全技术规范的行为或者在用的特种设备存在事故隐患的，应当以何种形式告之并督促有关单位（）。', '**A、发出书面特种设备安全监察指令**B、口头警告**C、向上级部门反映**D、直接予以罚款', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '110、特种设备出厂时，未按照安全技术规范的要求附有设计文件、产品质量合格证明、安装及使用维修说明、监督检验证明等文件的，由特种设备安全监督管理部门责令改正；情节严重的，可以按销售货值金额的多少比例进行罚款（）。', '**A、1%**B、5%以下**C、10%以下**D、30%以下', 'D', '', 0, 0);
