INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '113、压力容器的设计单位应由（）许可，方可从事设计活动。', '**A、地市级特种设备安全监督管理部门**B、省级特种设备安全监督管理部门**C、国务院特种设备安全监督管理部门**D、设计单位的省级主管部门', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '114、特种设备的维修单位必须经过（）许可，方可从事相应的维修活动。', '**A、地市级特种设备安全监督管理部门**B、省级特种设备安全监督管理部门**C、国务院特种设备安全监督管理部门D、设计单位的省级主管部门', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '115、特种设备安装、改造、维修施工前，应向（）书面告知后，即可施工。', '**A、直辖市或设区的市级特种设备安全监督管理部门**B、省级特种设备安全监督管理部门C、国务院特种设备安全监督管理部门**D、设计单位的省级主管部门', 'A', '', 0, 0);
