INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '117、客运索道、大型游乐设施的运营使用单位的主要负责人至少应当（）召开次会议，督促检查客运索道、大型游乐设施的安全使用。', '**A、每天**B、每周**C、每半月**D、每月', 'D', '', 0, 0);
