INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '132、蒸气锅炉在规定的给水压力和负荷范围内长期连续运行时，应保证的出口蒸气压力称为（）。', '**A、工作压力**B、设计压力**C、额定蒸气压力**D、最高压力', 'C', '', 0, 0);
