INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '139、特种设备作业人员在作业过程中发现事故隐患求者其它不安全因素应当（）向现场安全管理人员和单位有关负责人报告', '**A、处理区**B、及时**C、立即', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '140、《特种设备安全监察条例》是防止和减少（）保障人民群众生命和财产安全，促进经济发展', '**A、生产安全事故**B、事故**C.重特大事故', 'B', '', 0, 0);
