INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '4、特种设备使用单位应当对其使用的特种设备的（   ）进行定期换验、检修，并作出记录。', '**A、安全连锁装置**B、安全附件 **C、安全警示装置  **D、安全保护装置', 'AB', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '5、违反《特种设备安全法》规定，特种设备经营单位有下列行为之一的，责令停止经营，没收违法经营的特种设备，处三万元以上三十方元以下罚款；有违法所得的，没收违法所得：（    ）。', '**A、销售、出租未取得许可生产，未经检验或者检验不合格的特种设备的  **B、使用特种设备未按照规定办理使用登记的   **C、销售、出租国家明令淘汰、已经报废的特种设备，或者未按照安全技术规范的要求进行维护保养的特种设备的   **D、未按照安全技术规范的要求及时申报并接受检验的', 'AC', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '6、发生事故，对负有责任的单位除要求其依法承担相应的赔偿等责任外，依照下列规定处以罚款：（      ）。', '**A、发生一般事故，处十万元以上二十万元以下罚款   **B、发生较大事故，处二十万元以上五十万元以下罚款     **C、发生重大事故，处五十万元以上二百万元以下罚款  **D、发生特大事故，处二百万元以上五百万元以下罚款', 'ABCD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '7、违反《特种设备安全法》规定，特种设备生产、经营、使用单位有下列情形之一的，责令限期改正；逾期未改正的，责令停止使用有关特种设备或者停产停业整顿，处一万元以上五万元以下罚款：（      ）。', '**A、明知特种设备存在同一性缺陷，未立即停止生产并召回的   **B、未配备具有相应资格的特种设备安全管理人员、检测人员和作业人员的    **C、使用未取得相应资格的人员从事特种设备安全管理、检测和作业的   **D、未对特种设备安全管理人员、检测人员和作业人员进行安全教育和技能培训的', 'BCD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '8、办理起重机使用登记证时需要携带(      )材料。', '**A、特种设备作业人员证及复印件（使用单位盖公章）   **B、使用和运营的安全管理制度及目录复印件（使用单位盖公章)   **C、使用单位组织机构代码证或者产权所有（公民个人拥有）的身份证复印件（使用单位盖公章）  **D、产品质量合格证复印件', 'ABCD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '9、《特种设备安全监察条例》规定，从事特种设备作业的人员，未取得相应特种作业人员证书，上岗作业的（     ）。', '**A、由特种设备安全监督管理部门责令限期改正    **B、逾期未改正的，责令停止使用或者停产停业整顿  **C、处2000元以上2万元以下罚款  **D、直接追究作业人员责任', 'ABC', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '10、《特种设备安全监察条例》规定，对事故发生负有责任的单位，由特种设备安全监督管理部门依照下列规定处以罚款（       ）。', '**A、发生一般事故的，处10万元以上20万元以下罚款    **B、发生较大事故的，处20万元以上50万以下罚款    **C、发生重大事故的，处50万元以上200万元以下罚款', 'ABC', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '11、《特种设备安全监察条例》规定，特种设备出厂时，应当附有安全技术规范要求的（     ）等文件。', '**A、设计文件 **B、产品质量合格证明 **C、安装及使用维修说明**D、监督检验证明', 'ABCD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '12、《特种设备安全监察条例）规定，特种请备存在（      ）情况时，特种设备使用单位应当及时予以报废，并应当向原登记的特种设备安全监督管理部门办理注销。', '**A、严重事故隐患**B、无改造**C、维修价值**D、超过安全技术规范规定使用年限', 'ABCD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '13、《特种设备安全监察条例》规定，特种设备的生产、使用单位或者检验检测机构，拒不接受特种设备安全监督管理部门依法实施的安全监察的（      ）。', '**A、由特种设备安全监督管理部门责令限期改正**B，逾期烟未改正的，责令停产停业整顿，处2万元以上10万元以下罚款**C、触犯刑律的，依照刑法关于妨害公务罪或者其他罪的规定，依法追究刑事责以**D、有违法所得的，没收其违法所得', 'ABC', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '14、《特种设备安全监察条例》规定，特种设备使用单位应当对特种设备作业人员进行（    ）教育和培训。', '**A、特种设备安全**B、特种设备节能**C、特种设备使用**D、特种设备管理', 'AB', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '15、特种设备包括其(       )。', '**A、所用的材料**B、附属的安全附件**C、安全保护装置**D、与安全保护装置相关的设施', 'ABCD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '16、在特种设备生产（含设计、制造、安装、改造、维修)、充装、使用、检验检测活动中，因特种设备的不安全状态造成的特种设备事故通常是指特种设备本体、安全保护装置或附件（     ）失控或长时间中断运行等为主要特征的事故。', '**A、失效（泄漏、损伤、断裂、变形等)**B、爆炸**C、倾覆**D、坠落**E、碰撞**F、故障', 'ABCDEF', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '17、报告特种设备事故应当包括(       )内容。', '**A、事故发生的时间、地点、单位概况以及特种设备种类**B、事故发生初步情况，包括事故简要经过、现场破坏情况、已经造成或者可能造成的伤亡和涉险人数、初步估计的直接经济损失、初步确定的事故等级、初步判断的事故原因**C、已经采取的措施**D、报告人姓名、联系电话**E、其他有必要报告的情况。', 'ABCDE', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '18、对事故发生负有责任的单位的主要负责人未依法履行职责，导致事故发生的，由特种设备安全监督管理部门依照(     )规定处以罚款；属于国家工作人员的，并依法给予处分；触犯刑律的，依照刑法关于重大责任事故罪或者其他罪的规定，依法追究刑事责任。', '**A、发生一般事故的，处上一年年收入30%的罚款**B、发生较大事故的，处上一年年收入40%的罚款**C、发生重大事故的，处上一年年收入60%的罚款。保护装置和与安全保护装置相关的设施。', 'ABC', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '19、省安全条例所称特种设备，是指涉及生命安全、危险性较大的锅炉、（    ）、起重机械、客运索道、大型游乐设施和场（厂）内专用机动车辆，包括特种设备附属的安全附件、安全保护装置和与安全保护装置相关的设施。', '**A、压力容器（含气瓶）**B、压力管道**C、消防车辆**D、电梯', 'ABD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '20、特种设备（     ）人员，应当按照国家有关规定经特种设备安全监督管理部门考核合格，取得特种设备作业人员证书或者检验、检测人员证书。', '**A、作业人员**B、检验人员**C、检测人员**D、销售人员', 'ABC', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '21、气瓶充装单位在气体充装前，应当按照安全技术规范的要求对气瓶进行安全检查。禁止使用下列气瓶充装气体：（     ）', '**A、没有检验、检测标识的**B、超过定期检验周期的**C、经检验、检训不合格的**D、超过安全使用年限的。', 'ABCD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '22、锅炉（    ），应当符合安全技术规范的要求。', '**A、用水的水质**B、化学清洗**C、停炉保养', 'ABC', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '23、关于《浙江省特种设备安全管理条例》以下哪种说法是正确的（    ）。', '**A、违反本条例规定的行为，法律、行政法规已有法律责任规定的，从其规定**B、违反本条例第十二条第二款规定的，由县级以上特种设备安全监督管理部门责令限期改正；逾期未改正的，责令停止使用，处五万元以上二十万元以下罚款**C、违反本条例第十七条第五项、第六项规定的，由县级以上特种设备安全监督管理部门责令改正，处三万元以上三十万元以下罚款', 'ABC', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '24、高耗能特种设备，是指在使用过程中能源消耗量或者转换量大，并具有较大节能空间的（     ）等特种设备。', '**A、锅炉**B、换热压力容器**C、电梯**D、起重机', 'ABD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '25、高耗能特种设备生产含（    ）、使用、检验检测的节能监督管理，适用本办法。', '**A、设计**B、制造**C、安装**D、改造维修', 'ABC', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '26、高耗能特种设备的（    ）应当按照国家有关法律、法规、特种设备安全技术规范等有关规范和标准的要求，履行节能义务，做好高耗能特种设备节能工作，并接受国家质检总局和地方各级质量技术监督部门的监督检查。', '**A、生产单位**B、安装单位**C、使用单位**D、检验检测机构', 'ABCD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '27、国家鼓励高耗能特种设备的生产单位、使用单位应用（   ）、（   ）、（   ），提高特种设备能效水平。对取得显著成绩的单位和个人，按照有关规定予以奖励。', '**A、新技术**B、新工艺**C、新产品**D、新设备', 'ABC', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (0, 12, 0, '28、高耗能特种设备安全技术档案至少应当包括以下内容（    ）。', '**A、含有设计能效指标的设计文件**B、能效测试报告**C、设备经济运行文件和操作说明**D、日常运行能效监控记录、能耗状况记录', 'ABC', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '1、特种设备使用镶理规则适用于《特种设备目录》范围内的特种设备的安全与节能管理.', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '2、特种设备缏用单位是指具有特种设备使用管理权的单位,必须是汤权单位。', '**A、正确**B、错误', 'B', '', 1, 0);
