INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '16、及时纠正特种设备作业人员的违章行为是使用单位的义务。', '**A、正确**B、错误', 'A', '', 1, 0);
