INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '29、为公众提供运营服努的特种设备使用单位是指以特种设备作为经营工具的使用单位。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '30、特种设备使用单位应当配备安全管理负责人。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '31、特种设备使用单位安全管理负责人,应当取得相应的特种设备安全管理人员资格证书。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '32、特种设备使用单位安全管理负责人履行本单位特种设备安全的领导职责。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '33、特种设备使用单位安全管理负责人负责本单位特种设备安全管理制度具体实施。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '34、特种设备使用单位安全管理负责人组织进行隐患排查并负责处理意见的审批。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '35、特种设备使用单位安全管理负责人督促安全管理机构对本单位特种设备安全管理工作实施清况进行检查。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '36、特种设备续用单位安全管理负责人有权作出停止使用特种设备的决定。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '37、特种设备使用单位应当根据本单位特种设备的数量、特性等配备适当数量的安全管理员。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '38、专兼职安全管理员,必须取得相应的特种设备安全管理人员资格证书。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '39、使用额定工作压力大于或者等于2.5 MPa锅炉的必须配备专职安全管理人员。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '40、使用3台以上(含3台)第皿类固定式压力容器的必须配备专职安全管理人员。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '41、从事移动式压力容器或者气瓶充装的必须配备专职安全管理人员。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '42、使用5公里以上(含5公里)工业管道的必须配备专职安全管理人员。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '43、使用各类特种设备(不含气瓶)总量15台以上(含15台)的必须配备专职安全管理人员。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '44、高耗能特种设备使用单位应当配备节能管理人员,负责宣传贯彻特种设备节能的法律法规。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '45、严格执行特种设备有关安全管理制度,并且按照操作规程进行操作是特种设备安全管理人员的职责。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '46、特种设备作业人员应当取得相应的特种设备作业人员资格证书。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '47、特种设备安全管理人员应当按照规定填写运行、交接班等记录。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '48、锅炉作业人员应当严格执行锅炉节能管理制度,参加锅炉节能教育和技术培训。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '49、使用单位应当逐台建立特种设备安全与节能技术档案。 ', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '50、使用登记证不是特种设备安全技术档案的必备内容。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '51、安全技术档案中工业压力管道图样是指管道单线图(轴测图)。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '52、操作规程一般包括设备运行参数、设备使用地点、维护保养要求,巡回检査和异常情况处置规定,以及相应记录等。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '53、使用单位应当根据设备特点和使用状况对特种设备进行经常性维修改造。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '54、特种设备使用单位应当根据所使用特种设备的类别、品种和特性进行定期自行检查。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '55、客运索道试运行性检套应斜手进拉欢员公', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '56、锅炉使用单位应当破好锅炉水(介)质,处理和监测宓作保证水(介)质质量符合相关要求。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '57、电梯的渗营使用单位应当将安全使用说明、安全注意事项和弩际标志置于易于引起乘客沣意的位置!', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '58、容运索道的运营使用单位应当将安全使用说明、 安全注意事项和警示标志置于控制室内.', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '59、大型游乐设施的运营使用单位应当将安全使用说明,安全注意事项和警示标志置于操控室内', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '60、移动式(流动式)特种设备的定期检验可以在设备使用的所在地进行定期检验。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '61、特种设备达到设计使用年限需要继续使用的,经检验或者安全评估合格,需办理使用登记变更后方可使用。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '62、充装单位应当建立并落实充装前、充装后的检查与记录制度ゝ禁止对不符合安全技术规范要求的移动武压力容器和气瓶进行充装。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '63、移动式压力容器、气冲装单位，应当取得相应的制造许可资质,方可从事充装活动。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '64、安装单位负责塔式起重机、施工升降机在使用过程中的顶升行为,并且对其安全性能负责。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '65、对于整机出厂的特种设备,一般应当在投入使用后30日内办理使用登记。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '66、移动式大型游乐设施每次重新安装后,投入使用前,使用单位应当向产权地的登记机关申请办理使用登记。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '67、车用气瓶应当在投入使用前,向使用单位所在地的登记机关申请办理使用登记。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '68、国家明令淘汰或者已经报废的特种设备,不符合安全性能或者能效指标要求的符种设备,经安全评估后可以办理使用登记。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '69、首次定期检验的日期和实施改造,拆卸移装后的定期检验日期,由使用单位根据安全技术规范、监督检验报告和使用情况确定。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '70、特种设备需要变更使用单位,原使用单位应当持原使用登记证,使用登记表和有效期内的定期检验报告到登记机关办理变更。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '71、重新启用停用的特种设备时,使用单位应当进行目行检查,到使用登记机关办理启用手续.', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '72、对存在严重事故隐患,无改造,修理价值的特神设备,或者达到安全技术规就规定的报废期限的,应当及时予以报废,产权单位应当米取必叟措施消除该特种设备的使用功能。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '73、锅炉,压力容器、电梯,起重械,客运案道,大型游乐设施,场(厂)内专用机动车辆的作业人员及其相关管理人员,应当按照国家有天规定经特柳俊备安全监督管理部门考核合格,取得国家统一格式的特种作业人员证书,方可从事相应的作业或者管理工作。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '74、特种设备安全管理人员应当按照规定填写运行 交接班等记录。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '75、锅炉作业人员应当严格执行锅炉节能管理制瘴,渗加锅炉节能教育和技术培训,', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '76、特种设备生产使用单位的安全管理人员应当对本单位犠种设备的安全和节能全面负责。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '77、电梯、起重机械,大型游乐设施等为公众提供服务的特种设备运蒺使用单位,应当设置特种设备安全管理机构或者配备专职的安全管理人员。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '78、特种设备生产、使用单位应当聘(雇)用取得《特种设备作业人员证入的人员从事相关管理和作业工作,并对作业人员进行严格管理', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '79、特种设备生产、使用单位应当对作业人员进行安全教育和培训,保证特种设备作业人员具备必要的特种设备安全作业知识、作业技能和及时进行知识更新。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '80、电梯、客运索道、大型游乐设施等为公众提供服务的特种设备的运营使用单位,应当对特种设备的使用安全负责,设置特种设备安全管理机构或者配备专职兼职的特种设备安全管理人员。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '81、 《特种设备作业人员证》遗失或者损毁的,持证人应当及时报背发证部门,并在当地媒体予以公告。查证属实的,由发证部门补办证书。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '82、 申请人隐瞒有关情况战者提供虚假材料电请《特种设备作业人员证》的,不予受理或者不予批准发证,并在3年内不得再次申请《特种设备作业人员证》。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '83、电梯使用管理者(指具有电梯管理权利和义务的单位或个人)按照“谁所有谁负责谁共有谁负责、谁主管谁负责”的原则确定。未落实电梯使用管理者的电梯,不得投入使用。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '84、种设备属于共有的,共有人可以委托物业服务单位或者其他管理人管理特种设备,受托人履行本法规定的特种设备使用单位的义务,承担相应责任。共有人未委托的,由共有人或者实际管理人履行管理义务,承担相应责任.', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '85、高耗能特种设备使用单位可以将节能管理职责交由特种设备安全管理机构承担。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '86、复审不合格、逾期未复审的,其《特种设备作业人员证》予以注销。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '87、任何单位和个人不得非法印制、伪造、涂改、倒卖、出租或者出借《特种设备作业人员证》。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '88、电梯使用单位应指定负责电梯管理的人员保管电梯的三角钥匙,机房钥匙和电梯电源钥匙,非专门人员不得使用。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '89、作业人员未能参加用人单位培训的,不可以选择专业培训机构进行培训。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '90、作业人员培训的内容无需按照国家市场监督管理总局制定的相关作业人员培训考核大纲等安全技术规范执行。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '91、锅炉、压力管道安全管理人员要求16周岁(含,下同)以上,男60周岁以下,女55周岁以下。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '92、大型游乐设施安全管理人员要求20周岁以上,男60周岁以下,女55周岁以下。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '93,电梯安全管理人员要求高中以上文化程度,2年以上本专业或安全管理工作经力相关工作或者安全管理工作的经历。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '94、场车安全管理人员应具有高中以上(含高中)学历,具有3年以上(含)3年场车相关工作或者安全管理工作的经历。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '95、压力容器安全管理人员要求中专以上文化程度,1年以上相关工作经历。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '96、电梯使用单位的安全管理人员负责与有资质的安装,改造、维修单位或制造单位签订电梯维保合同,并对维保合同的执行情况进行监督。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '97、特种设备安全管理人员负责向特种设备检验检测机构在特种设备安全检验合格有效期届满前半个月提出定期检验要求,保证在用设备持证运行的有效衔接。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '98、特种设备使用单位应当根据本单位特种设备的数量、特性等配备适当数量的专兼职安全管理员人专兼职安全管理员应取得特种设备安全管理员证书。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '99、按要求设置安全管理机构的使用单位安全管理负责人,应当取得相应的特种设备安全管理人员资格证书。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '100、使用电站锅炉或者石化与化工成套装置的使用单位无需设置特种设备鹭全管理机构,只需配备专职安全管理人员。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '101、特种设备安全管理负责人是指使用单位最高管理层中主管本单位特种设备使用安全管理的人员。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '102、特种设备使用单位应当根据本单位特种设备的数量、特性等配备适当数量的安全管理员.', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '103、对于氧舱、大型游乐饧旆江客运索道;安余阀篝作业人员较少的项目,由省级市场监督管理部门发证。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '104、为保证安全,使用单位制订的各项规章制度,如岗位责任制度、交接班制度等,作业人员必须严格遵守这些制度。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '105、特种设备作业人员人员的培训考核应当先于设备安装,可以委派已参加培训的人员参与设备的安装调试过程,以便于及早熟悉设备的性能状态。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '106、作业人员未能参加用人单位培训的,可以选择专业培训机构进行培训。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '107、特种设备作业人员应当作业时随身携带证件,并自觉接受用人单位的安全管理和质量技术监督部门的监督检查。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '108、特种设备安仝管理机构的职责是贯彻执行国家特种设备有关法律、法规和安全技榨规范及相关标准,负责落实使用单位的主要义务。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '109、作业人员老核发证工作由地方县级以上(含县级)市场监督管理部门分级负责。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '110、 国家市场监督管理总局负责全国作业人员考核工作的监督管理,督管理部门负责本辖区内作业人员考核工作的监督管理和考试机构进行监督检查。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '111、特种设备作业人员考核规则适用于国家市场监督管理总局制定发布的《特种设文作业人员资格认定分类与项目》范围内特种设备作业人员的考核工作。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '112、 作业人员考核发证部门委托考试机构组织考核,不得自行组织考核。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '113、 考试机构具有满足考试需要的考试管理人员和考评人员。考评人员应当具备大夫及以上学历、从事本专业5年及以上、具有丰富的实践操作经验,熟恭考试程序、考试管理考试内容及评分要求等。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '114、 考试机构按照考试大纲的要求进行理论知识考试和实际操作技能考试。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '115、 考试现场应当配备信息化人证比对系统,并且留存考试影像资料;鼓励在考试机位设置自动视频抓拍系统.', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '116、作业人员考试及发证程庭请,受理交考试公示和发证.', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '117、特种设备作业小员鲜龄在18周岁及以上且不超过60周熬具有完全民事行为能力。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '118、发证机关在收到申请后10个工作日内,应当作出是否受理的决庭。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '119、考试机构应当至少提前2个月公布考试时间、 地点、作业项目等事项', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '121、持证人员应当在持证项目的有效期届满1个月前,向工作所在地或颅籍所在地的发证部门提出复审申请。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '122、发证部门应当在发证后的15个工作日内,将发证人员相关信息日传国特种设备公示信息香询平台”', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '123、考武机构应当建立作业人员考试档案; 内容应当包括考试人员名单及成绩、考试部试卷、操作技能考试记录, 现场考试记录(含现场考试影像)等。 档案保存斯奎少10年。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '124、申请复审时,持证人员应提交 《特种设备作业人员证》原件。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '125、华人民共和国特种设备制造许可证》锅炒分为AB、两个制造许미级别,A,由国家市场监管总局许可B,曲省级市场监管部门许可。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '126、锅炉A,级制造许可级别,许可范围为额定出口压力小于等于2.5MPA,的蒸汽利热水锅炉;有机热载体锅炉。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '127、锅炉A,级制造许可级别,许可范围为额定出口压力大于2.5MPA,的蒸汽和热水锅炉。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '128、我国对压力容器制造实行制造许可制度,即在我国境内制造,使用的压力容器，制造企业必须取得《中华人民共和国特种设备制造许可证》 (以下简称制造许可证）未取得《制造许可证》的企业,其产品不得在境内销售,使用,压力容器划分为A,B网制造许可证)未取造许可级别。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '129、使用单位一定要购买和使用有《制造许可证》.制造单位制造的压力容器产品。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '130、在我国境内制造的压力容器产品必须经过企业所在地的省级市场监管部门授权的检验单位进行监督检验。 监检是在压力容器制造单位质量检验、检查与试验合格的基础上进行的过程监督和满足基本安全要求的符合性验证。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '131、压力管道元件是管道系统的基本单元,直接影响着系统的安全和正常运行,所以在选用时必须选择具有压力管道元件制造资格的单位生产的元件。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '132、压力管道元件是管道系统的基本单元,直接影响着系统的安全和正常运行,在选用时不一定要选择具有压力管道元件制造资格的单位生产的元件。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '133、生产单位应当任命项目负责人,负贵电梯施工现场的技术指导、协调等工作。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '134、垂直电梯的制造单位,应当具有轿厢(含轿架)制造,控制柜组装的能力。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '135、申请电梯制造(含安装、修理、改造)许可的单位,应当试制造所申请相应许可子项目的样机各1台.', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '136、 起重机械制造单位应当具有独立完成主要受力结构件制造的能力。制造单位的钢材预处理、涂装、机械加工《理化检验下无损检测等下作不允许外委。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '137、生产单位应当任命技术负责人,全面负责本单位场()内专用机动车辆制造、改造和修理活动中的技术工作', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '138、从事场(厂)内专用机动车辆整车装配的每一处制造地址,其广房以及日常生产所用的生产设备《工艺装备、 检测仪器、试验装置应当分别满足相应资源条件的要求。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '139、为督促特种设备生产、经营和使用单位落实安全主体责任,各级特种设备安全监督管理部门依据特设法实施现场安全监督检查,分为日常监督检查和专项监督检查。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '140项监督检查是指按照 《特种设备现场检查规则》规定的检查计划 检查项目、检查内容,对被检查单位实施的监督检查。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '141、日常监督检查是指根据各级人民政府及其所属有关部门的统一部署人或由各级监管部门组织的夕针对具体情况,在规定的时间内,对被检查单位的特定设备或顼目实施的监督检查。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '142、 属于重点监督检查的特种设备使用单位,每年日常监督检査次数不得少于2次。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '143,特种设备现场窗全监督检查实行抽查方式。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '144、 被检查单位无正当理由拒绝检查人员进入特种设备生产、使用场所检查。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '145、 特种设备专项监督检查包括重点时段监督检查 ,专项整治监督检查和其他专项监督检査。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '146、专项整治监督检查是根据国家或地区重大活动及节假日的安全保障需要,针对特定单位、设备和项目开展的监督检查。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '147重点时段监督检查是根据国家或地区重大活动及节假日的安全保障需要,针对特定单位、设备和项目开展的监督检查.', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '148对在用特种设备安全状况的检查实行抽查方式,对一个使用单位,少抽查2台(套)在用特种设备。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '149,日常监督检杳的项目和内容,按照《特种设备生产单位现场安全监督检查项目表》、“特种设备使用单位现场安全监督检查项目表》 的规定执行。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '150,重点监督检查的特种设备使用单位目录,由市级监管部门确定。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '151、使用非法生产特种设备的属于特种设备使用单位重大问题。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '152、 使用超期未检、 经检验检测判为不合格且限期未整改的或复检不合格特种设各的属于特种设备使用单位重大问题。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '153、 检查人员在监督检查中,应当遵守相关的安全管理要求,保证自身安全。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '154、 生产单位和使用单位应主动配合接受安全监督检查。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '155、 被检查单位因故不能提供有关书证材料的,检查人员可以书面通知被检查单位后补。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '156、 检查人员无权对有证据表明不符合安全技术规范要求或者存在严重事故隐患的特种设备实施查封、扣押。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '157、 检查人员有权根据举报或者取得的涉嫌违法证据,查阅,复制特种设备生产经营、使用单位的有关合同、发票、账簿以及其他有关资料。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '158、检查人员无权对流入市场的达到报废条件或者受经报废的特种设备实施查封、扣押。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '159、 检查人员有权对违反 《特种设备安全法》 《特种设备安全监察条例》和特种设备地方法规,以及其他特种设备行政规章规定的行为作出行政处讨决定。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '160、被检查单位无正当理由拒绝检查人员进入特种设备生产、使用场所检查。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '161、对现场监督检查不予配合,拖延、阻碍正常检查,可以认定为拒不接受依法实施的监督检查，但不予处罚。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '162、检查时发现违反《特种设备安全法》和《特种设备安全监察条例》规定和安全技术规范要求的行为或者特种设备存在事故隐患时,检查人员应当下达《特种设备罢全监察指令书》,责令被检査单位立即或者限期采取必要措施予以改正,消除事故隐患。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '163、检查记录应当由被检查单位参加人员和检查人员双方签字。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '164、 对特种设备实施查封或扣押前,检查人员无需向本监管部门负责公报告。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '165、查封、扣押的期限不得超过30天。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '166、 检查提出整改要求的,检查人员应当在被检查单位提交整改报告后3个工作日之内对隐患整改情况进行复查。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '167、 检查提出整改要求的被检查单位未提交整改报告、整改期限届满后5个工作日之内对隐患整改情况进行复查。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '168、复查可以通过现场检查材料核查等形式实施。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '169、被检查单位无正当理由拒绝检查人员进入特种设备生产,使用场所检查,对玑场监督检查不予配合,拖延、阻碍正常检查,可以认定为拒不接受依法实施的监督检查,应当依据《特种设备安全法》第九十五条的规定予以处罚。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '170、重点时段监督检查各级监管部门的相关负责人应当带队参加。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '171、 承办特种设备违法案件的机构不负责对隐患整改情况进行复查。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '172,发现被检查单位或者人员涉嫌构成犯罪的,应当按照《行政执法机关移送涉殊心罪案件的规定》,移送公安机关调查处理。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '173,对特种设备出租单位的监督检查参照出租单位的监督检查规定实施。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '174、检查人员应当在检查及整改结束后,将检查信息录入特种设备动态监管信息化系统。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '175、锅炉外部检验是指锅炉在运行状态下对锅炉当前使用管理状况进行的检验。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '176、在用锅炉的外部检验一般每两年进行一次;内部检验一般毎年进行一次,成套装网中的锅炉结合成套装道的大修周期进行,电站锅炉结合锅炉检修同期进行,一般每3~6年进行一次。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '177、 成套装置中的锅炉和电站锅炉由于检修周期等原因不能按期进行锅炉定期检验时，使用单位在佣保锅炉安全运行(或者停用)的前提下,经过使用单位安全管理负责人审批后，可以适当延期安排检验,但是不得连续延期。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '178、未发现影啊锅炉安全运行的问题或者对问题进行整改合格的检验结论为基本符合要求。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '179、锅炉定期检验项目的顺库为手外部检验,内部检验和水(耐)压试验在同一年进行时,一般首先进行内部检验然后再进行水(耐)压试验,外部检验~', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '180、压力容器的年度检查是指为了确保压力容器在检验周期内的安全而实施的运行过程中的在线检查以每年至少两次。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '检查和压力容器安全附件检查等。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '182、压力容器检验安全状况等级为1,2级的,一般每6年一次检验。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '183、安全状况等级为4级的,应当对缺陷进行处理,否则不得继续使用。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '184、使用单位应当在压力容器定期检验有效期届满前2个月向检验机构提函定期检验要求。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '185、年度检查也要对压力容器安全状况等级进行评定。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '186、使用单位没有按照规定进行压力容器年度检查的,可缩短定期检验周期。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '187、 改变使用介质并且可能造成腐蚀现象恶化的压力容器, 可缩短定期检验周期。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '188、材质劣化现象比较明显的压力容器,不必缩短定期检验周期。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '189、压力管道年度检查是在运行条件下对在用下业管道进行的检验,每年至少1次。年度检查一般以宏观检查和安全保护装置检验对芏,`必要时进行测厚检查和电阻值测量。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '190、 使用单位应当制定压力管道年度检查管理制度,年度检查工作由使用单位安全管理人员组织经过专业培训的人员进行,使用单位也可将在线检验工作委托给具有压力管道检验资格的机构。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '191,安全附件与仪表检验不合格的管道,不允许投入使用。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '192,压力管道定期检验结论分为“允许使用”、 “降压使用”, “进行合于使用评价”三种.', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '93,压力管道安全等级状况3级的,检验周期一般不超过6年.', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '194, 如果出现了有关电梯生产和检验的新技术、新材料、新工艺等影响检验规则技术指标和要求的特殊情况,国家质检总局可以根据具体情况, 提出相应要求。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '195,电梯使用单位应当在电梯使用标志所标注的下次检验日期届满前1个月,向检验机构申请定期检验。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '196,电梯检验项目分为A,BC,D四个类别。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '197、电梯现场检验至少由2名具有电梯检验员或以上资格的人员进行。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '198,电梯现场检验时,检验人员可以进行电梯的修理,、调整等工作。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '199、 对自动扶梯与自动人行道整机进行检验时,检验现场应放置表明正在进行检验的警示标识,并在出入口设置围栏。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '200、对于定期检验的电梯,如果使用单位拟实施改造或者重大修理进行整改,或者扣做停用、报废处理,则应当在 《通知书》 上签署相应的意见,并且在规定的时限内反馈给检验机构,同时按照相关规定,办理对应的相关手续。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '201、新制造的冶金桥式起重机不需要安装安全监控管理系统。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '202、 监检人员在从事监检时,应当在施工单位提供的设计文件、工作见证(检查报告试验报告、 记录表、 卡等,下同)上签部捅认!并且注明监检确认友式(资料审查、现场监督实物检查)、具体内容和签字日期。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '203、检验机构应当根据实施施工监检的起重机械状况, 按照《起重机械安装改造重大修理监督检验规则か的要求, 制定包括检验程序、 监检项目及其要求 监检记录等在内的监检方案(或者监检工作指导书),直接实施。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '204、在监检过程中发现一般问题,监检人员应当向施工单位发出 《特种设备监督检验意见通知书》发现严重问题,检验机构应当向施工单位出具 《特种设备监督检验联络单》。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '205、《起重机械安装改造重大修理监督检验规则》规定:检验机构应当按照特种设备安全信息化管理要求,及时汇总、统计、上报有关监检数据。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '206、重机械定期检验规则规定:检验程序至少包括检验前准备、现场检验!检验结果汇总、结论判定和出具检验报告等。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '207、根据《特种设备目录》,场(厂)内专用机动车辆,是指除道路交通、侬用车辆以外仅在工厂厂区,旅游景区,游乐场所等特定区域使用的专用机动车辆危包括机动工业车辆和非公路用旅游观光车辆。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '208、场(厂)内专用机动车辆旁余技术监察规程中所指风车包括可拆卸式属具。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '209、场(厂)内专用机动车辆安全技术监察规程中所指蓄电池观光列车的驱动方式为电动机,且其动力源为锂电池组。', '**A、正确**B、错误', 'A', '', 1, 0);
