INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (1, 12, 0, '10、申请《特种设备作业人员证》的人员应当符合下列条件:(  )', '**A、年龄在20周岁以上**B、身体健康并满足申请从事的作业种类对身体的特殊要求**C、有与申请作业种类相适应的文化程度**D、具有相应的安全技术知识与技能', 'BCD', '', 2, 0);
