INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '93、底盘由传动系、行驶系、转向系和制动系等组成。', '**A、正确**B、错误', 'A', '', 1, 0);
