INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '95、叉车限速阀的主要作用是确保货叉在下降时 速度不致太快。', '**A、正确**B、错误', 'A', '', 1, 0);
