INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '97、客运索道,是指动为驱动,利用柔性绳索牵引箱体等运载工具运送人员的机电设各包括客运架空索道、客运缆车、客运拖牵索道等。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '98、非公用客运索道和专用于单位内部通勤的客运索道纳入特种设备监管范围。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '99、客运索道按其运动方式分可以分为往复式和循环式二大类。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '100、循环式索道按其运行方式可分为连续循环式、 间歇循环式、 脉动循环式三种。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '101、脉动式循环索道的运行特点是运行-停止-运行。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '102、客运索道由线路设施、 驱动系统、运载工具、安全保护系统,运行辅助设施五部分组成。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '103、客运索道使用单位在国家法定节假日或者开展大型活动等客运索道乘坐人员高峰期前,应当对客运索道进行全面检查维护,并加强日常检查和安全值班。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '104、客运索道的日常检查一般分为日检、月检、年检三种类型。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '105、客运索道的年检往往与设备大修仪器进行。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '106、客运索道使用单位根据客运索道的使用维护说明书及使用情况、年限制定有针对性的检修及维保计划,严格落实执行。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '107、为便于救援,客运索道使用现场的显著位置应有运行线路图。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '108、客运索道的站房应有正常照明和备用照明。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '109、客运索道安全检查记录应包含以下内容:钢丝绳检测、探伤记录;主要部件材质证明和探伤报告;土建备案书。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '110、客运索道控制室视野应开阔,能观察到乘客进出站情况。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '111、大型游乐设施,是指用于经营目的,承载乘客游乐的设施,其范围规定为设计最大运行线速度大于或者等于2m/s,或者运行高度距地面高于或者等干2m的载人大型游乐设施。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '112、大型游乐设施种类繁多,结构复杂,总共有13大类,分别是:观览车类,滑行车类,架空游览车类,陀螺类,飞行塔类、转马类,自控飞机类,寒车类,小火车类类,碰碰车类、滑道类、水上游乐设施、无动力游乐设施。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '113、水上游乐设施类分为峡谷漂流系列、水滑梯系列、碰碰车系列三个品种。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '114、无动力游乐设施分为蹦极系列、滑索系列、空中飞人系列、系留式观光气球系列四个品种。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '115、设备生产单位应当按照安全技术规范和使用维护说明书的要求,开展设备运营前试运行检查、日常检查和维护保养,定期安全检查并如实记录。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '116、大型游乐设施运营使用单位对日常维护保养和试运行检查等自行检查中发现的异常常情况,应当及时处理。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '117、运营使用单位进行本单位设备的维护保养工作,应当按照产品维护说明要求配备作业人员、必备工具和设备。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '118、运营使用单位应当在大型游乐设施的设备本体上张贴乘客须知、安全注意事项和警示标志,注明设备的运动特点、乘客范围、禁忌事宜等。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '119、运营使用单位应当制定应急预案,建立应急救援指挥机构,配备相应的救援人员、营救设备和急救物品。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '120、运营使用单位应当加强营救设备、 急救物品的存放和管理, 对救援人员定期进行专业培训,每季至少对每台(套)大型游乐设施组织1次应急救援演练。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '121、运营使用单位应当设置专门的安全管理机构并配备安全管理人员，或者配备专职的安全管理人员,并保证设备运营期间,至少有2名安全管理人员在岗。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '122、对超过整机设计使用期限仍有修理、改造价值可以继续使用的大型游乐设施,运营使用单位应当按照安全技术规范的要求通过检验或者安全评估,继续使用。', '**A、正确**B、错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '123、运营使用单位租借场地开展大型游乐设施经营的,应当与场地提供单位签订安全管理协议,落实安全管理制度。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '124、场地提供单位应当核实大型游乐设施运营使用单位满足相关法律法规以及本规定要求的运营使用条件。', '**A、正确**B、错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '1、 接受特种设备安全监察的锅炉范围为:容积( )的承压蒸汽锅炉; 出口水压大于或者等于O.1MPa(表压),且额定功率大于或者等于0.1MW的承压热水锅炉; 有机热载体锅炉。', '**A、小于或者等于30L**B、大于或者等于30L**C、大于或者等于25L**D、大于或者等于50L', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '2、 出口水压大于或者等于O.1MPa(表压),且额定功率( )的承压热水锅炉应当接受安全监察.', '**A、大于或者等于O.1MWB、小于或者等于10MWC大于或者等于 10MW**D、小于或者等于 O.1MW', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '3、( )的有机热载体锅炉应该接受安全监察。', '**A、出口大于或者等于O.1MPaB、大于或者等于0.1MW C所有D、大干或者等于30L', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '4、 锅炉可以用来().', '**A、生产热水B、生产蒸汽C,加热有机热载体D、生产一定温度和一定压力的蒸汽、热水或一定温度的有机热载。', 'D', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '5、 锅炉的那个环节必须要接受安全监察().', '**A、设计、制造**B、使用、检验、修理**C、设计、制造、安装,使用,检验、修理、改造**D、使用', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '6、下列不属于锅炉主要受压部件的是().', '**A、锅筒、锅壳、下降管**B、炉门圈、喉管、冲天管**C、炉排、引风机、安全阀**D、过热器、减温器、再热器', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '7、工业锅炉的三大安全附件是指().', '**A、水位计、水位警报器、排污阀**B、压力表、水位计、水位警报器**C、安全阀、压力表、水位计**D、安全阀、水位警报器、超压报警', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '8、额定蒸气压力小于或等于3,82MPa时,安全阀喉径不应小于()mm。', '**A,15**B,20**C,2**D.30', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '9、安全阀应该多长时间校验一次().', '**A、两年**B、一个定检周期**C、每年至少一次**D、没有故障不必校验', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '10、关于安全阀的安装, 下列叙述不正确的是( )。', '**A、安全阀应垂直安装在锅筒、集箱的最高位置**B、在安全阀与锅筒或集箱之间,应装有取用蒸汽的出口管和阀门**C、安全阀与锅炉的连接管,其截面积应不小于安全阀的进口截面积**D、 安全阀一般应装设排气管,排气管应直通安全地点,并有足够的截面积,保证排气畅通', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '11、 选用压力表时应注意压力表的量程一般为工作压力的( )。', '**A、1-2倍 **B、1.5-3倍**C3-4倍**D1倍', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '12、 使用单位必须制定有机热载体炉使用操作规程。操作规程应当包括以下哪些内容()?', '**A、有机热载体炉启动、运行等操作方法和应注意事项**B、防火、灭火措施**C、停炉、紧急停炉等操作方法**D、有机热载体炉启动、运行、停炉,紧急停炉和防火、灭火措施等操作方法和应注意事项', 'D', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '13、 下列锅炉型号中是链条炉排锅炉的是( )。', '**A、DZL4-1.25-W**B、SHS10-1.25/250-A2**C、QXW2.8-0.7/95/70-AII**D、DZH4-1.25-W', 'A', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '14、 水垢对锅炉的影响,下列叙述不正确的是().', '**A、积垢到一定程度后,将造成浪费燃料、降低锅炉热效率**B、导致垢下金属腐蚀,伯不影响锅炉出力**C,降低锅炉使用寿命**D、危机锅炉的安全', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '15、蒸汽锅炉在运行中,遇到下列哪些情况,不属于立即停炉的情况( )。', '**A、水位表或者安全阀全部失效**B、设置在汽空问的压力表全部失效**C、燃烧不正常**D、燃烧设备损坏、炉墙倒塌敷者锅炉构梨彼烧红等严重威胁锅炉安全运行', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '16、处理锅炉满水事故可以().', '**A、尽快灭火**B、加强通风**C、开启排污阀或开启紧急放水阀**D、利用锅筒上的空气阀排汽', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '17、属于国家强制监察的压力容器为().', '**A、工作压力大于或者等于0.1Mpa**B、容积大于或者等于0、03m''并且内直径大于或者等于150mm**C、盛装介质为气体、液化气体以及最高工作温度高于或等于标准沸点的液体**D、上述三条缺一不可。', 'D', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '18、 下列关于设计压力的描述错误的是()。', '**A、设计压力应略高于容器顶部可能出现的最高压力**B、装有安全泄压装置的压力容器,设计压力应不低于安全阀的开启压力和爆破牌片装置的爆破压力**C、盛装液化气体的容器,无保温装置的,设计压力不低于所装液化气体在常温下的饱和蒸气压力**D、有可靠保温设施的,设计压力不低于其在试验实测的最高温度下的饱和蒸气压力。', 'C', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '19、 高温高压染色机为( )压力容器。', '**A反应**B、换热**C分离**D、储存', 'B', '', 0, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '20、分汽缸为( )压力容器。', '**A反应**B、换热 **C分离**D、储存', 'C', '', 0, 0);
