INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '6、压力容器的使用单位,应当在工艺操作规程和岗位操作规程中,明确提出压力容器安全操作要求,其内容至少包括（）:', '**A、压力容器的操作工艺参数(含工作压力、最高或者最低工作温度)**B、压力容器的岗位操作方法(含开、停车的操作程序和注意事项)**C、操作人员应遵守的劳动纪律**D、压力容器运行中应当重点检查的项目和部位,运行中可能出现的异常现象和防止措施,以及紧急情况的处置和报告程序', 'ABD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '7、压力容器使用单位应当对压力容器及其（）进行经常性日常维护保养,对发现的异常情况,应当及时处理并且记录。', '**A、安全附件**B、安全保护装置**C、测量调控装置**D、附属仪器仪表', 'ABCD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '8、压力容器年度检査至少包括()', '**A、压力容器安全管理情况检查**B、压力容器本体及运行状况检查**C、压力容器内部腐蚀情况**D、压力容器安全附件检查', 'ABD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '9、下列哪些管道应接受安全监察。(）', '**A、毒性程度为极度危害介质的管道**B、火灾危险性为甲、乙类介质的管道**C、最高工作压力力大于等于0.1MPa,输送介质为(汽)体、液化气体的管道**D、最高工作压力大于等于O.IMPa,输送介质为可燃、易爆、有毒、有腐蚀性的或最高工作温度等于高于标准沸点的液体管道', 'ABCD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '10、压力管道是指利用一定的压力,用于输送气体或者液体的管状设备,其范围规定为最高工作压力大于或者等于（）MPa(表压)的气体,液化气体,蒸汽或者可燃,易爆、有毒、有腐蚀性、最高工作温度高干或者等于标准沸点的液体介质,且公称直径大于或者等于（）mm的管道。公称直径小于150mm,且其最高工作压力小于1.6MPa(表压)的输送无毒、不可燃、无腐蚀性气体的管道和设备本体所属管道除外。', '**A,1.0**B,O.1**C,25**D、50', 'BD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '11、关于压力管道的维护保养,下列表述正确的是:（）', '**A,使用单位应当对管道进行经常性维护保养,并且做出记录,存入管道技术档系**B、管道的重大维修应当由有资格的安装单位进行施工**C、管道的重大维修一般无需书面告知管道使用登记机关和申请监督检验**D、管道内部有压力时,一般不得对受压元件进行重大维修', 'ABD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '12、关于压力管道的使用,做法正确的是:（）', '**A、在用管道发生故障、异常情况,查明原因。对故障、异常情况,以及检查、定期检验中发现的事故隐患或者缺陷,应当及时采取措施,消除隐患后,方可重新投入使用**B、使用单位应当对停用或者报废的管道无需采取安全措施**C、建立定期自行检查制度,检查后应当做出书面记录,书面记录至少保存3年**D、使用单位应当对管道操作人员进行管道安全教育和培训,保证其具备必要的管道安全作业知识', 'ABD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '13、 电梯是以载人为主要用途的设备,所以对其的基本要求是（）。', '**A、安全可靠**B、方便快捷**C、舒适**D、豪华', 'ABC', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '14、发生电梯电击事故的原因主要有（）。', '**A、电气设备金属外壳未接地或接地不良**B、电线、电缆绝缘保护层破损**C、作业人员违反操作规程带电作业**D、制动器工作失效', 'ABC', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '15、电梯的重量平衡系统通常由( )构成。', '**A、对重装置**B、曳引钢丝绳**C、曳引机**D、补偿装置', 'AD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '16、自动扶梯在入口处的附近应设置( )和“禁止使用手推车”的使用须知标牌。', '**A、“小孩必须拉住”**B、“宠物必须抱着”**C、“握住扶手带”**D、“站立时面朝运动方向,脚须离开梯级边缘”', 'ABC', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '17、造成室内作业触电事故的主要原因有（）', '**A、未穿戴必要的安全防护用品**B、起重机未设置可靠的保护接地装置**C、缺乏安全操作知识和电气控制原理知识**D、缺乏电气检查安全要领', 'ABCD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '18,下列（）属于吊具。', '**A、吊钩**B、抓斗**C、电磁吸盘**D、真空吸盘', 'ABCD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '19、吊钩破断事故的主要原因有()。', '**A,吊钩材质有缺陷**B、吊钩因长期磨损危险断面减小达到报废极限仍然使用**C、吊钩经常超载使用造成疲劳破坏**D、吊钩开口度或扭转变形量达到报废极限仍然使用', 'ABCD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '20、造成脱绳事故的主要原因有（）。', '**A、物件的捆绑方法和要领不当**B、吊装重心选择不当,造成偏载起吊**C、吊装重心选择不当,造成吊装中心不稳**D、物件遭到碰撞,冲击,振动等而揺摆不定', 'ABCD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '21、场(厂)内专用机动车辆有（）工作特点。', '**A、工作环境差异大,工况恶劣**B、同类场(厂)内机动车辆的规格差别很大**C、一机具有多种可换工作装置**D、各类产品之间具有使用成套性', 'ABCD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '22、下列（）系统属于内燃机的组成部分。', '**A、点火系**B、制动系**c、润滑系**D、冷却系', 'ACD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '23、造成场(厂)内专用机动车辆事故的原因主要有（）。', '**A、违章驾车**B、疏忽大意**c、车况不良**D、道路条件差', 'ABCD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '24、场(厂)内专用机动车辆的总体性能和( )因素有关。', '**A、车辆总体布置**B、装卸条件及运行状况**C、车辆总体构造**D、关键部件的性能', 'ABCD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '25、在客运索道的出入口处等显著位置应当张贴( ),内容应包括乘客适应范围、禁忌事宜等,并按照安全技术规范和有关标准的要求悬挂警示标志。', '**A、乘客须知**B、安全使用说明**C、安全注意事项**D、游览图', 'ABC', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '26、客运索道使用单位应当按照安全技术规范和使用维护说明书的要求,开展设备运营前（）,并如实记录。', '**A、试运行检查**B、日常检查和维护保养**C、 定期自行检查**D、安全教育', 'ABC', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '27、客运索道使用单位不得使用( )的客运索道。', '**A、未经监督检验**B、未经定期检验**C、监督检验、定期检验不合格', 'ABC', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '28、客运索道出厂时,应当随附安全技术规范要求的（）等。', '**A、设计文件**B、产品质量合格证明(以部件出厂的应提供部件产品质量合格证明)**c、安装图纸及说明**D、使用维护保养说明**E、型式试验合格证明**F、无损检测报告', 'ABCDEF', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '29、大型游乐设施安装单位在安装施工前,应当确认( )等土建工程符合土建工程质量监督管理要求。', '**A、场地**B、设备基础**C、预埋件**D、环境', 'ABC', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '30、大型游乐设施运营使用单位应当按照安全技术规范和使用维护说明书的要求,开展设备运营前( )并如实记录。', '**A、试运行检查**B、日常检查和维护保养**C、定期安全检查**D、安全校验', 'ABC', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (2, 12, 0, '31、大型游乐设施出厂时,应当附有( )等文件。', '**A、产品质量合格证明**B、设计文件鉴定报告**C、型式试验合格证明**D、安装及使用维护说明书', 'ABCD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '1、重大事故由国务院特种设备安全监督管理部门会同有关部门组织事故调查组进行调查。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '2、特种设备发生事故后，事故单位应立即启动事故应急预案，组织抢救，防止事故扩大，减少人员伤亡和财产损失，并及时向事故发生地县以上特种设备安全监督管理部门和有关部门报告。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '3.移动式压力容器、机电类特种设备异地发生其故后，业主或者聘用人员应当立即报告当地质量技术监督行政部门，并同时报告设备使用注册登记的质量技术监督行政部门。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '4、按照安全技术观范的要求应当进行型式试验的特种设备产品、部件或者试制特种设备新产品、新部件，未进行整机或者部件型式试验的，由特种设备安全监督管理部门责令限期改正；逾期未改正的，处2万元以上10万元以下罚款。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '5、对电梯的安全运行情况进行跟踪调查和了解时，发现存在严重事故隐患，未及时向特种设备安全季度管理部门报告的由特种设备安全监督管理部门责令限期改正、逾期未改正的，予以罚款。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '6、使用未经定期检验或者检验不合格的特种设备的由特种设备安全监督管理部门责令限期改正；逾期未改正的，处2000以上2万元以下罚款。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '7、特种设备使用单位擅自动用、调换、转移、损毁被查封、扣押的特种设备或者其主要部件的，由特种设备安全监督管理部门责令改正，处5万元以上20万元以下罚款。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '8、对事故发生负有责任的单位，由特种设备安全监督管理部门依照下列规定处以罚款，发生一般事故的，处10万元以上30万元以下罚款。 ', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '9、对发生较大事故负有责任的单位，由特种设备安全监督管理部门处10万元以上50万元以下罚款。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '10、特种设备使用单位的主要负责人对特种设备事故隐瞒不报、谎报或者拖延不报的应受到4000元以上2万元以下罚款处罚。触犯刑律的，依照刑法关于重大责任事故罪或者其他罪的规定，依法追究刑事责任。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '11、从事特种设备作业的人员，未取得相应特种作业人员证书，上岗作业的，应受到2000元以上2万元以下罚款处罚', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '12、客运索道、大型游乐设施每日投入使用前，未进行试运行和例行安全检查，并对安全装置进行检查确认的，被要求责令改正，逾期未改正的，处1万元以上5万元以下罚款。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '13、未制定特种设备事故应急专项预案逾期未改正的且情节严重的，应停止使用或停产停业整顿。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '14、使用未经定期检验或者检验不合格的特种设备的，由特种设备安全监督管理部门责令限期改正：逾期未改正的，处2000元以上2万元以下罚款。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '15、特种设备使用单位的主要负责人在本单位发生特种设备事故时，不立即组织抢救或者在事故调查处理期间擅离职守或者逃逸，触犯刑律的，依照刑法关于重大责任事故罪或者其他罪的规定，依法追究刑事责任。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '16、重大事故由国务院特种设备安全监督管理部门会同有关部门组织事故调查组进行调查。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '17、特种设备发生事故后，事故单位应立即启动事故应急预案，组织抢救，防止事故护大、减少人员伤亡和财产损失，并及时向事故发生地县以上特种设备安全监督管理部门和有关部门报告。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '18、事故报告应当及时、准确、完整，任何单位和个人对事故不得迟报、漏报，或者瞒报。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '19、发生一般事故，由设区的市级人民政府负责特种设备安全监督管理部门会同有关部门组织事故调查组进行调查。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '20、查清事故发生前的特种设备状况是事故调查组的职责。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '21、调查事故根据需要一般可设管理组、技术组、综合组。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '22、事故调查组各组长可由负责事故调查的特种设备安全监督管理部门确定。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '23、事故调查组中综合组主要负责事故调查的协调工作。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '24、综合组不需要配合技术组、管理组开展事故的调查，安排事故调查的保障工作。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '25、事故调查组成员在事故调查过程中，应当格尽此守，客观公正，实事求是。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '26、遵守事故调查组的纪律，保守事故调查的秘密，在事故调查处理工作结束前，不得擅自对外发表意见。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '27、事故调查组组长：不用负责组织事故调查处理的特种设备安全监督管理部门主管领导担责。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '28、事故调查，主要审核涉嫌触犯刑律的证据资料，不用负责向组织事故调查处理的特种设备安全监督管理部门汇报。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '29、事故调查，对需要进行技术鉴定的，及时提请组织调查的特种设备安全监督管理部门协调落实；不需要按照规定时限组织完成事故调查工作。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '30、管理组组长：由具有安全管理工作经验的行政人员担任，负责和协调管理组的调查工作，组织提出管理方面的事故分析意见或报告。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '31、技术组组长：由具有较丰富专业技术工作经验的人员担任，负责和协调技术组的调查工作，提出技术方面的事故分析意见或报告。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '32、综合组组长：由事故发生地人民政府或行政管理部门的人员担任，负责综合组的工作，组织安排和协调后勤保障和善后处理工作。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '33、事故调查开展现场情况了解、现场询问和现场勘验等工作并及时准确做好记录。只需要注意调查人员安全防护，不需要与有关方面做好沟通和协调。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '34、事故调查可以直接询问当事人和报案人，掌握重要现场知情人员，并做好登记。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '35、事故调查听取有关人员的介绍，检查现场保护情况，做出标识，绘制现场简图并书面记录，拍摄记录现场音像资料。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '36、事故现场经初步应急处置结束后，可以单独及时开展现场询问工作，并做好特种设备事故调查询问笔录。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '37、事故调查应根据事故的初步状况，迅速确定和落实调查的区域、对象、问题和的询问顺序。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '38、事故调查询问时必须由2名(或以上)调查人员进行。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '39、事故询问前，调查人员应向被询问人告知，被询问人可以拒绝配合。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '40、事故处理询问过程中，可以先不做笔录，只用录音代替。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '41、事故调查需要注意记录事故现场概貌、局部和定位等情况。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '42、事故调查必要时还可绘制模拟工艺流程图等。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '43、事故调查需要对安全保护装置、安全附件及附属设备失效或损坏情况的调查。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '44、事故调查需要对现场伤亡人员情况的调查。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '45、事故调查应当关注和搜集事故应急救援现场相关部门对事故现场的检测或测定资料。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '46、事故调查需要对当事人或见证人员提供的情况进行现场比核实。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '47、事教调意不需要查阅企业相关档案资料。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '48、事故调章查阅生产方面资料时，主要包括设备的设计文件、制造尽资料、安装。改造、维修资料
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '49、事故调查不需要看设备及相关主要零部件销售合同', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '50、企业作业人员的培训、教育、持证上岗、职业健康安全防护的情况可以缺少相应项目。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '51、事故调查在对调查过程当中的记录应当及时、规范、全面、客观、准确。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '52、根据调查组要求，通过现场调查还不能确定事故性质时，需要深人进行技术分析来判定事故发生条件原因的，可进行技术鉴定。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '53、现场取样可以在没有相应指导下标出其部位，并对这些部位进等保护处理。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '54、取样时可以没有事改调查组人员现场监督标识，包装封存。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '55、事故调查中技术检验、试验和鉴定的主要内容应当围绕事故现场破坏主要形式和关键项目，按照委托的要求进行。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '56、事故调查在必要时可进行比对或者模拟试验，也可以任意扩大范围和项目。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '57、事故调查涉及倒塌或者失稳的事故也不需要对承重支撑结构进行必要的检验或者鉴定。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '58、机电类特种设备主要对被破坏以及对事故发生产生直接影响承载结构、传动机构、制动装置、电气控制、主要连接等件、安全装置等进行单项或部务进存技术检验、安经和鉴定。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '59、调查组认为需要对事故造成的经济损失进行评估的，事故调查组可以提出委托的项目、要求和被委托的资产评估单位，由组织事故调查的部门进行书面委托。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '60、承担技术检验、试验和鉴定及经济损失评估的单位不需要具备相应条件的机构，只需要与事故各方无利害关系。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '61、根据引发事故发生的因素或事件对事故发生后果的作用程度，事故的原因划分为主原因和次要原因、无关原因', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '62、在分析事故时，通过对事故发生的征兆、时刻、位置、状态、痕迹等事实证据进行分析和确认，不需要找出与事故有关的各种因素之间因果关系和逻辑关系。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '63、通常应当从直接原因入手，逐步深入到间接原因，经过对事故后果有影响的重要因素分析后，分清其作用程度的主次，找出事故的主要原因和次要原因。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '64、确定事故原因，应当召开事故原因分析会。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '65、事故原因分析会由事故调查组组长主持，事故调查组全体成员参加，必要时邀请有关专家参加。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '66、事故调查可以不尊重技术专家意见，只需要采取少数服从多数的原则，确定事故原因。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '67、在责任事故中，根据责任主体或行为人的行为与事故发生后果的练习，分为直接责任人和间接责任人、无关责任。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '68、根据责任主体或行为人的行为对事故后果所起作用程度，事故责低分为主要责任和次要责任。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '69、根据《条例》、《规定》的规定，认定事故性质，认定责任主体或为人的责任。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '70、事故调查可以不根据事故调查的情况，认定事故的种类和性质。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '71、事故调查根据责任主体或行为人的行为年事故结果有无直接关系、分清事故的直接原因和间接原因，确定事故中的直接责任者和间接责任人。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '72、根据事故主体或行为人的行为对事故发生结果的作用及其与事故的相关程度，分清事故主要原因和次要原因，确认事故的主要责任者和次要责任者、无责任者。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '73、特种设备使用单位应当运用现代安全管理原理、方法和手段，分析和研究各种不安全因素，对特种设备进行使用安全管理评价。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '74、使用单位或其委托的第三方机构作为评价主体对特种设备使用安全管理进行评价。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '75、使用单位委托的第三方机构不能作为评价主体对特种设备使用安全管理进行评价。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '76、评价工作通过资料核对和抽查考核两种方式进行。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '77、开展评价活动的评价组可以由不熟悉特种设备使用和管理的人员组成。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '78、评价活动只需要对设备安全管理进行量化评价。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '79、中风险特种设备包括公众聚集场所使用的乘客电梯、自动扶梯和自动人行道。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '80、中风险特种设备包括GB1级燃气管道、GB2级热力管道和GC2级工业管道。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '81、抽查时，气瓶充装单位的气瓶数量按1000只为1台压力容器折算，不足1000只按1台折算。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '82、对使用单位安全管理情况进行评价评分，评价内容应包括管理机构、管理制度、管理人员、技术档案、应急管理、历年事故和违法违规样况。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '83、根据设备安全管理评价得分和单位安全管理评价得分情况，将使用单位判定为A类、B类、C类、D类四个类别。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '84、根据设备安全管理评价得分和单位安全管理评价得分情况，将使用单位判定为C类标为黄色。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '85、一年内发生一般特种设备事故或两年内发生较大及以上特种设备事故且未查明原因未整改到位的或存在违法违规情况的使用单位则判定为A类使用单位。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '86、使用单位应定期开展分类评价活动，原则上评价结果为A类的单位一年至少一次', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '87、使用单位应定期开展分类评价活动，原则上评价结果为C类的单位半年至少一次。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '88、使用单位的特种设备使用管理情况有重大变化（如设备数量及状态、管理人员和作业人员数量变动较多以及设备发生事故等情况）时，应及时开展分类评价活动。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '89、评价过程中发现重大风险和隐患，使用单位应单独建立台帐和档案，并限期落实整改措施，消除风险和隐患，做到闭环管理，建立风险分级管控和隐患排查治理双重预防机制。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '90、海因里希法则可以用于企业的安全管理上，即在一件重大的事故背后必有29件轻度的事故，还有300件潜在的隐患。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '91、特种设备安全会议后不需要形成书面记录。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '92、发现隐患的部门应及时填写隐患发现的时间、地点以及主要问题描述。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '93、发现的隐患的部门应及时向部门安全管理人员以及公司特种设备安全管理负责人报告。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '94、设备运行记录及交接班记录不是隐患的发现来源。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '95、从专项检查和安全评价中可以发现隐患。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '96、在使用过程中发现安全险患，作业人员或者维保人员应在第一时间采取应急措施。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '97、隐患整改和确认的相关资料应及时存入安全管理档案。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '98、重大隐患的监控方案只需报告单位安全管理负责人。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '99、对每本单位日常检查、定期检查、检验检测机构定期检验和政府特种设备管理部门安全监察中发现的特种设备险患，特种设备安全管理机构（安全管理人员）应当指定部门或人员负责隐患治理工作，保证必要的隐患进行整治资金，并及时安排时间进行整治，必要时停止运行该设备。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '100、对于安全监察机构《安全监察指令书》或检验检测机构《检验结果通知单》提出的问题，使用单位应及时填报《特种设备安全隐患整改回复报告书》，以书面形式将整改情况报相关安全监察或检验检测机构确认。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '101、对于因生产等原因不能（或难以）及时整活的隐患，都必须及时予以消除。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '102、特种设备报废后应当向原登记的特种设备安全监督管理部门办理注销。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '103、若隐患没有及时整改，书面告知相关部门负责人后，无需停止使用设备。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '104、发现隐患后，由安全管理人员确认隐患大小，并判定是否立即停止作业、整改。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '105、特种设备使用单位是具有特种设备使用管理权的单位或者具有完全民事行为能力的自然人，一般是特种设备的产权单位（产权所有人），也可以是产权单位（产权所有人)通过符合法律规定的合同关系确立的特种设备实际使用管理者', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '106、特种设备使用单位是具有特种设备使用管理权的单位，一般是特种设备的产权单位，也可以是产权单位通过符合法律规定的合同关系确立的特种设备实际使用管理单位，不可以为自然人。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '107、特种设备监管机构是负责特种设备安全监督管理职责的部门、相关行业主管部门，乡（镇）人民政府以及街道办事处、开发区(园区)管理机构等人民政府的派出机关(机构）的总称。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '108、开展特种设备使用安全管理分类评价的第三方机构，必须是依法设立的，具备一定数量特种设备相关专业人员，能够独立开展公正评价的服务机构。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '109、开展特种设备使用安全管理分类评价时，使用经检验判为不合格且限期未整改的或复检不合格特种设备的使用单位，为判定C类单位。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '110、开展特种设备使用安全管理分类评价时，使用国家明令淘汰或已经报废的特种设备的使用单位，应判定为D类单位。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '111、开展特种设备使用安全管理分类评价时，使用经检验判为不合格且限期末整改的或复检不合格特种设备的使用单位应判定D类单位。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '112、开展特种设备使用安全管理分类评价时，使用超过特种设备的规定参数范围使用的使用单位，应判定为C类单位。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '113、开展特种设备使用安全管理分类评价时，使用被监管机构责令整改而未予整改的特种设备的使用单位，应判定为D类单位。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '114、特种设备作业人员在作业过程中发现事故隐患或者其他不安全因素：应当立即向现场安管理人员和单位有关负责人报告。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '115、构建双重预防机制就是针对安全生产领域、“认不清、想不到”的突出问患，强调安全生产的关口前移，从隐患排查治理前移到安全风险管控。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '116、不需要强化风险意识偶尔分析事故发生的原因，也可以抓住事故关键环节。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '117、特种设备双重预防机制是指特种设备的安全风险分级管控和隐患排查治理。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '118、双重预防机制着眼于安全风险的有效管控，紧盯事故隐患的排查治理虽然是一个常态化运行的安全生产管理系统，但并不可以有效提升安全生产整体预控能力。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '119、通俗说、双重预防机制就是构筑防范生产安全事故的一道防火墙。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '120、2016年8月28日国务院安委办印发了《标本兼治遏制重特大事故工作指南》。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '121、安全风险是来源于可能导致人员伤亡或财产损失的危险源或各种危险有害因素，是事故发生的可能性和后果严重性的组合而事故隐患是风险管控失效后形成的缺陷或漏洞。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '122、风险分级管控就是根据安全风险的大小（意味着危害程度)，将风险划分不同等级，按照风险等级对不同等级的风险采取不同的管控措施，配备不同的管控资源，实现最终降低风险的目的。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '123、隐患排查治理是用系统安全的分析方法，对企业生产经营场所或活动中的人、机、料、法、环进行综合分析。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '124、隐患排查治理是安全风险分级管控的前提和基础。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '125、隐患排查治理是安全风险分级管控的强化与深入，通过隐患排查治理工作，查找风险管控措施的失效、缺陷或不足，采取措施予以整改。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '126.安全风险分级管控和隐患排查治理共同构建起预防事故发生的双重机制，构成两道保护屏障，有效遏制重特大事故的发生。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '127.隐患就是在某个条件、事物以及事件中所存在的不稳定并且影响到个人或者他人安全利益的因素，它是一种潜藏着的因素。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '128.安全风险来源于可能导致人员伤亡或财产损失的危险源或各种危险有害因素，是事故发生的可能性和后果严重性的组合，而事故隐患是风险管控失效后形成的缺陷或漏洞，两者是相同的概念。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '129、事故隐患主要来源于风险管控的薄弱环节。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '130：海因里希法则是美国著名安全工程师海因里希(Herbert William Heinrich)提出的300：29：3法则。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '131、严格落实特种设备安全管理主体责任，通过自行检查、安全评价、接受监察、定期检验等方式排查特种设备安全隐患争取将隐患消除在萌芽状态，从而确保特种设备安全运行。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '132、海因里希法测是德国人海因里希通过分析工伤事故的发生概率的一个规则。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '133、任何特种设备使用单位都需要建立特种设备安全管理机构。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '134、特种设备安全管理负责人应定期召开会议，听取使用部门及安全管理部门关于特种设备隐患的发现情况，检查治理情况，并形成书面记录。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '135、可以从设备运行记录及交接班记录，且常维保记录，目常检查记录，设备异常情况记录，传项检查，定期检验，外部检查（包含政府和上级部门的专项督查等》，安全评价等方面发现隐患。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '136、发现隐患后，应及时填写隐患发现的时间、地点以及主要问题描述，及时向部门安全管理人可以及公司特种设备安全管理负责人报告。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '137、只要不影响生产，有点小隐患都可以将就不停止作业。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '138、被用过程中发现的安全隐患，作业人员或者维保人员应在第一时间采取应急措施，并且按程序向特种设备安全管理机构（安全管理人员）或者特种设备安全管理负责人报告。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '139、只需要认真对待检验检测机构定期检验和政府特种设备管理部门安全监察中发现的特种设备隐患即可，对生产过程中发现的隐惠无需理会。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '140、对于因生产等原因不能（或难以）及时整治的隐患一应进行风险评估，对重大的、难以容忍的隐患，必须及时予以消除。其它严重程度的，应当制定监控方案，落实监控措施、监控责任、整改期限。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '141、隐患整改和确认的相关资料应及时存入安全管理档案。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '142、对于仅依靠本单位力量难以消除的隐患和一旦发生事故可能造成严重影响的重大隐患，应书面报告当地政府和特种设备安全监督管理部门申请协助消除。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '143、对于特种设备存在严重事故隐患，无改造、维修价值，或者超过特种设备设计使用年限或安全技术规范规定使用年限，或者经检验检测不合格又无法消除安全隐患，应当及时予以报废，并应当向原登记的特种设备安全监督管理部门办理注销。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '144、事故隐患来源于安全风险的管控失效或弱化，安全风险得到有效管控就会不出现或少出现隐患。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '145、特种设备使用单位应当制定事故应急专项预案，并不定期进行事故应急演练。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '146、应急管理是对重大事故的全过程管理，贯穿于事故发生前、后的各个过程，充分体现了“预防为主，常备不懈”的应急思想。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '147、应急管理是一个动态的过程，包括预防、准备和恢复三个阶段。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '148、在应急管理中预防有两层含义，一是事故的预防工作，即通过安全管理和安全技术等技术，尽可能地防止事故的发生，实现本质安全；二是在假定事故必然发生的前提下，通过预先采取的预防措施，达到降低或减少事故损失的有效方法。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '149、事故应急预案在应急系统中起着关键作用，它明确了在突发事故发生之前、发生过程中以及刚刚结束之后，谁负责做什么、何时做，相应的策略和资源准备等。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '150、应急风险评估是检验、评价和保持应急能力的一个重要手段。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '151、根据游乐设施的特点，其危险目标和可能故障主要体现在四大方面，即外部环境因素（包自然灾害人为破坏、机械故障、电气故障和管理操作失误。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '152、特种设备风险评估是以诱发特种设备事故的各个因素为条件、以影响因素发展成各类事故的可能性为依据，以事故后果造成的综合损失为评估指标，、对各类特种设备的安全程度做出风险识别、分析和评价。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '153、特种没备事故应急预案为有效预防和控制特种设备可能发生的事故入最大程度减少特种设备事故发生的可能性及其可能造成损害而预先制定的工作方案。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '154、特种设备事故应急演练针对特种设备可能发生的事故场景，依据特种设备事故应急预案而开展的应急活动。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '155、领导及机构就是成立以单位法人为领导，相关部门或人员组成的应急预案编制工作组。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '156、制定工作计划就是结合本单位各部门职能分工，明确编制任务和职责分工，制定工作计划。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '157、对单位基本概况进行调查，包括但不限于单位经济性质、隶属关系、生产规模、人员数量。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '158、对单位基本概况进行调查，包括但不限于单位的组织架构。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '159、对单位特种设备基本情况进行汇总，包括但不限于特种设备的种类、数量、介质、用途及其分布。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '160、对单位特种设备基本情况进行汇总，包括但不限于涉及特种设备配置的局部布置图、应急设施（备）平面布置图等。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '161、对单位特种设备所处周边环境状况进行调查，包括但不限于单位特种设备所处地区的地理、气象、水文、灾害等自然环境情况。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '162、对单位特种设备所处周边环境状况进行调查，包括但不限于单位特种设备所处周边区域人口密度与数量、主要建筑物性质（相邻社区、学校、机关、重要基础设施等）、单位周边建筑物的距离情况、周边可利用的安全、消防，救护设备设施分布情况。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '163.收集与预案编制工作相关的法律法规、技术标准、应急预案、国内外同类型单位事故资料、特种设备技术资料等有关资料。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '164、风险评估工作要求就是运用风险预测的方法，识别单位特种设备存在的风险因素确定各类特种设各可能发生的事故类型和后果，进行风险分析和评价，作为应急预案编制的依据。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '165、本单位特种设备风险评估的结果应明确内容包括各类特种设备可能产生的事故原因、后果与影响范围。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '166、本单位特种设备风险评估的结果应明确内容包括自然灾害可能造成特种设备事故的说明。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '167、依据风险评估的结果，对单位特种设备现有的事故预防措施、应急人员、应急设施、装备与物资等应急能力进行评估，明确应急救援的需求和不足，提出资源补充、合理用和资源集成整合的建议方案，完善应急救援资源。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '168、应急能力评估包括但不限于应急队伍建设情况，各种专业人员分布与分工情况。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '169、应急能力评估包括但不限升各种保障制度，包括应急控制制度、应急人员队伍建设与管理制度（包括应急人员安全防护、群众安全防护措施等）仪器设备检查与日常维护制度、经费保障制度、培训制度、演习制度等。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '170、应急能力评传包括但不限于外部资源及能力，包括单位所在地政府部门应急指挥系统状况、周边可以借助的社会资源分布情况、联系方式等。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '171、在风险评估和应急能力评估的基础上，按照本单位实际情况及应急预案体系要求编制相应的特种设备事故应急预案，对应急预案主要内容要素的采纳，应根据单位应急预案体系要求进行编写：并与所在地政府的相关应急预案及单位的综合应急预案衔接。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '172、应急预案编制完成后，应进行评审。评审通过后，应由单位法人签发实施。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '173、应顾预案的总则主要内容包括编制依据简述应急预案编制所依据的有关法律、法规、规章、\\安套技术规范、标准等。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '174、应急预案的总则主要内容包括适用范围说明应急预案适用的范围》包括特种设备事故的类型、级别）', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '175、应急预案的总则主要内容包括基本情况阐述单位的基本概况、特种设备基本情况、周边环境状况和可利用的安全、消防、救护设备设施分布情况及重要防护目标调查结果。可配合图表进行表述。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '176、应急预案的总则主要内容包括风险描述阐述存在的特种设备风险评估结果，可能发生事故的后果和波及范围。可配合图表进行表述。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '177、应急预案阐述存在的特种设备风险因素结果，可能发生事故的后果和波及范围。可配合图表进行表述。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '178、应急预案要明确应急总指挥机构组织形式、构成部门（单位）或人员及日常工作机构和专家技术组组成。可用结构图形式表示。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '179、明确应急救援指挥机构的指挥人员、相关部门或人员的相应职责及安全要求。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '180、根据事故类型和应急工作需要，可设置事故现场应急救提指挥机构和相应的指挥人员、抢险救灾，医学救护，通信联络、事故处置、善后工作等应急救授工作小组，并明确各小组的工作任务和安全职责。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '181、根据风险评估和应急能力评估的结果，明确控制特种设备事故发生的技术和管理措施。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '182、根据国家有关法律法规，将特种设备事故分为不同等级，按级别明确特种设备事故预警的条件、方式和方法。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '183、明确持种设备事故发生后，向所在地人民政除、负责特种设备安全监督管理的部门和负有安全生产监管职责的其他政府部门报告事故信息的方式、流程，内容和时限。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '184、明确对媒体和公众发布信息的程序和原则，组织信息发布和舆论引导工作。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '185、应急响应级别按照分级负责的原则，明确不同响应级别的负责人员。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '186、根据特种设备事故的级别，明确现场应急指挥、应急措施、资源调配、应急避险、扩大应急等响应处置程序。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '187、明确事故现场监测设备、器材和现场监测人员及其安全防护措施，监控和分析事故所造成的危害程度、事故是否得到有效控制、是否有扩大危险趋势，及时提供准确信息。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '188、明确事故现场租有关证据的保护措施、按照ISC02Q《特种设备事故报告和调查处理导则》等有关规配合协助相关部门查找事故原因，进行事故调处、、提出防范整改措施。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '189、明确单位各专业应急队伍及负责人的通讯联络方式，应附人员联络表', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '190、明确应急救援需使用的应急物资和装备的类型、数量、性能、管理责任人等内容。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '191、明确应急专项经费来源、使用范围、数和监督管理措施。鼓励投保特种设备安全责任险，建特种设备保险事故补偿机制。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '192、跟据单位特种设备应急工作需求而确定的技术保障、治安保障、需要请求支援的外部机构、组织的名单和联络方式等其他相关保障措施。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '193、依据对单位有关员工能力的评估结果，制定应急预案培训教育计划（包括应急培训方式、考核、记录表效果评价等)。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '194、特种设备事故应急演练宜每年应不少于2次，演练单位应做好记录备查。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '195、明确应急预案实施和生效的具体时间。', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '196、明确应急预案负责制定与解释的部门', '**A.正确**B.错误', 'A', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '197：特种设备事故造成50人以上死亡！或者160人以上重伤（包括急性工业中毒)，或者1亿元以上直接经济损失的为特别重大事敌。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '198、500MW以上锅炉爆炸的为特别重大事故。', '**A.正确**B.错误', 'B', '', 1, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '199、压力容器、压力管道有毒介质泄漏，造成15万人以上转移的为特别重大事故。', '**A.正确**B.错误', 'A', '', 1, 0);
