INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '21、中风险特种设备包括:（）', '**A、额定热功率Q>4.2MW液相有机热载体锅炉**B、液化天然气储罐**C、快开门式压力容器**D、盛装易燃易爆及有毒介质的第Ⅱ类压力容器', 'AD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '22、特种设备双重预防机制是指特种设备的（）。', '**A、安全风险分级管控**B、隐患排查治理**C、制度**D、流程', 'AB', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '23、特种设备使用单位的隐患类型主要包括（）等。', '**A、设备本体隐患**B、管理制度隐患**C、设备使用隐患**D、设备环境隐患', 'ACD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '24、隐患的发现主要来自但不限于以下环节所产生的记录:（）专项检查，定期检验，外部检查(包含政府和上级部门的专项督查等)，安全评价。', '**A、设备运行记录及交接班记录**B、日常维保记录**C、日常检查记录**D、设备异常情况记录', 'ABCD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '25、双重预防机制有哪些原则（）。', '**A、风险优先原则**B、系统性原则**C、全员参与原则**D、持续改进原则', 'ABCD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '26、特种设备发生事故,事故发生单位应当采取哪些措施?（）', '**A、立即启动事故应急预案,组织抢救,防止事故扩大,减少人员伤亡和财产损失**B、保护事故现场,等待有关部门处理事故**C、及时、如实向有关部门报告,不得隐瞒不报、谎报或者拖延不报**D、隐瞒情况,躲避有关部门展开事故调查。', 'ABC', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '27、在进行量化打分时,对抽查的特种设备的使用管理情况分种类逐台进行评价打分,评价的内容应当包括（）等内容。', '**A、特种设备操作规程**B、使用登记**C、出厂资料**D、安全保护装置', 'ABD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '28、在进行单位安全管理评价时,对使用单位安全管理情况进行评价评分,评价内容应包括()等内容。', '**A、管理机构**B、历年事故和违法违规情况**C、企业占地面积**D、管理人员管理制度', 'ABD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '29、未对特种设备作业人员进行特种设备安全教育和技能培训的可能受到何种处罚?（）', '**A、责令限期改正**B、责令停产**C、处一万元以上五万元以下罚款**D、发生严重事故的,追究主要责任人刑事责任', 'ABC', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '30、除“违反”型隐患外，由某些因素而引起的安全生产事故隐患三种现实表现是（）。', '**A、环境的危险状态**B、设备的危险状态**C、人的不安全行为**D、管理上的缺陷', 'BCD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '31、关于“海因里希法则”以下说法正确的是（）。', '**A、是美国著名安全工程师海因里希（Herbert WiLLiam Heinrch）提出300：29:1法则**B、这个法则意为;当一个企业有300起隐患或违章，非常可能要发生29起轻伤或故障，另外还有一个重伤、死亡事故**C、这一法则完全可以用于企业的安全管理上，即在一件重大的事故背后必有29件轻度的事故，还有300件潜在的隐患', 'ABCD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '32、下列定义正确的是（）。', '**A、风险是不确定性对目标的影响，伤害发生的概率和伤害严重程度的总和**B、风险评估包括风险识别、风险分析和风险评价的全过程**C、风险识别是发现、确认和描述风险的全过程**D、风险评价是对比风险分析结果和风险准侧，以确定风险和/或其大小是否可以接受或容忍的过程', 'ABCD', '', 2, 0);
INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '33、特种设备事故应急救援技术装备清单包括（）。', '**A、应急处置通用技术装备**B、应急处置特殊技术装备**C、应急处置高新技术装备**D、应急处置安全防护用品', 'ABCD', '', 2, 0);
