INSERT INTO gtpx_db.problems_temp (chapter, course, num, title, choices, answers, images, category, level) VALUES (3, 12, 0, '35、应急处置特殊装备包括（）。', '**A、消防设备**B、医疗救护设备**C、工业设备**D、专用工具', 'ABCD', '', 2, 0);
